import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaCheck, FaTimes, FaKey, FaUser, FaRedo, FaExclamationTriangle } from 'react-icons/fa';

const DebugAuth = () => {
  const [tests, setTests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errorCount, setErrorCount] = useState(0);
  const [backendUrl, setBackendUrl] = useState('http://localhost:5000');
  const [debugLog, setDebugLog] = useState([]);

  useEffect(() => {
    runTests();
  }, []);

  const addLog = (message, type = 'info') => {
    const timestamp = new Date().toLocaleTimeString();
    setDebugLog(prev => [...prev, { message: `[${timestamp}] ${message}`, type }]);
  };

  const runTests = async () => {
    setLoading(true);
    setDebugLog([]);
    const testResults = [];
    let errors = 0;

    addLog('Starting authentication debug tests...');

    // Test 1: Check localStorage token
    const token = localStorage.getItem('token');
    const hasToken = !!token;
    if (!hasToken) errors++;

    addLog(`Token check: ${hasToken ? 'Token found' : 'No token found'}`);

    testResults.push({
      name: 'Token in localStorage',
      status: hasToken,
      message: token ? `Token found (${token.length} chars)` : 'No token found'
    });

    // Test 2: Check backend health
    try {
      addLog(`Testing backend health at: ${backendUrl}/health`);
      const healthResponse = await axios.get(`${backendUrl}/health`);
      const isHealthy = healthResponse.status === 200;
      addLog(`Backend health: ${isHealthy ? 'OK' : 'Not OK'} - ${healthResponse.data?.status || 'No status'}`);

      testResults.push({
        name: 'Backend Health',
        status: isHealthy,
        message: healthResponse.data?.status || 'Backend is running'
      });
    } catch (error) {
      errors++;
      addLog(`Backend health check failed: ${error.message}`, 'error');
      testResults.push({
        name: 'Backend Health',
        status: false,
        message: `${error.message} - URL: ${backendUrl}`
      });
    }

    // Test 3: Validate token with backend
    if (token) {
      try {
        addLog('Validating token with backend...');
        const authResponse = await axios.get(`${backendUrl}/api/auth/me`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        const userEmail = authResponse.data.user?.email || authResponse.data.email || 'Unknown';
        addLog(`Token validation successful. User: ${userEmail}`);

        testResults.push({
          name: 'Token Validation',
          status: true,
          message: `Valid - User: ${userEmail}`
        });
      } catch (error) {
        errors++;
        const errorMsg = error.response?.data?.error || error.message || 'Unknown error';
        addLog(`Token validation failed: ${errorMsg}`, 'error');
        testResults.push({
          name: 'Token Validation',
          status: false,
          message: errorMsg
        });
      }
    } else {
      errors++;
      addLog('Skipping token validation - no token available', 'warning');
      testResults.push({
        name: 'Token Validation',
        status: false,
        message: 'No token to validate'
      });
    }

    // Test 4: Test note creation
    if (token) {
      try {
        addLog('Testing note creation...');
        const testNote = {
          title: 'Test Note from Debugger',
          content: 'This is a test note created by the authentication debugger.',
          tags: ['test', 'debug'],
          isEncrypted: false
        };

        const noteResponse = await axios.post(`${backendUrl}/api/notes`, testNote, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        // Check the response structure
        const noteId = noteResponse.data.id || noteResponse.data._id || noteResponse.data.note?.id;
        const responseData = JSON.stringify(noteResponse.data, null, 2);
        addLog(`Note creation response: ${responseData}`);

        if (noteId) {
          addLog(`Note created successfully. ID: ${noteId}`);
          testResults.push({
            name: 'Note Creation',
            status: true,
            message: `Created note - ID: ${noteId}`
          });

          // Clean up - delete the test note
          try {
            addLog(`Cleaning up test note (ID: ${noteId})...`);
            await axios.delete(`${backendUrl}/api/notes/${noteId}`, {
              headers: { 'Authorization': `Bearer ${token}` }
            });
            addLog('Test note cleaned up successfully');

            testResults.push({
              name: 'Note Cleanup',
              status: true,
              message: 'Test note deleted successfully'
            });
          } catch (cleanupError) {
            errors++;
            addLog(`Note cleanup failed: ${cleanupError.message}`, 'error');
            testResults.push({
              name: 'Note Cleanup',
              status: false,
              message: cleanupError.response?.data?.error || cleanupError.message
            });
          }
        } else {
          errors++;
          addLog('Note creation succeeded but no ID found in response', 'warning');
          addLog(`Full response: ${responseData}`);

          testResults.push({
            name: 'Note Creation',
            status: false,
            message: 'Created but no ID returned. Check response structure.'
          });
        }
      } catch (error) {
        errors++;
        const errorMsg = error.response?.data?.error || error.message || 'Failed to create note';
        addLog(`Note creation failed: ${errorMsg}`, 'error');

        if (error.response?.data) {
          addLog(`Error details: ${JSON.stringify(error.response.data)}`, 'error');
        }

        testResults.push({
          name: 'Note Creation',
          status: false,
          message: errorMsg
        });
      }
    } else {
      addLog('Skipping note creation - no token available', 'warning');
      testResults.push({
        name: 'Note Creation',
        status: false,
        message: 'Skipped - No authentication token'
      });
    }

    setTests(testResults);
    setErrorCount(errors);
    setLoading(false);
    addLog(`Tests completed. ${errors} error(s) found.`);
  };

  const clearToken = () => {
    localStorage.removeItem('token');
    addLog('Token cleared from localStorage');
    window.location.reload();
  };

  const resetAuth = () => {
    localStorage.clear();
    sessionStorage.clear();
    addLog('All storage cleared, redirecting to login');
    window.location.href = '/login';
  };

  const copyToken = () => {
    const token = localStorage.getItem('token');
    if (token) {
      navigator.clipboard.writeText(token);
      addLog('Token copied to clipboard');
      alert('Token copied to clipboard!');
    }
  };

  const inspectResponse = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token available');
      return;
    }

    try {
      addLog('Manually testing note creation to inspect response...');
      const testNote = {
        title: 'Manual Test Note',
        content: 'Testing response structure',
        tags: ['manual-test'],
        isEncrypted: false
      };

      const response = await axios.post(`${backendUrl}/api/notes`, testNote, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      console.log('Full note creation response:', response);
      console.log('Response data:', response.data);

      addLog('Response logged to console. Check browser console for details.', 'info');
      alert(`Response structure logged to console.\n\nData keys: ${Object.keys(response.data).join(', ')}`);

      // Clean up
      if (response.data.id || response.data._id) {
        const noteId = response.data.id || response.data._id;
        await axios.delete(`${backendUrl}/api/notes/${noteId}`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        addLog('Test note cleaned up');
      }
    } catch (error) {
      addLog(`Manual test failed: ${error.message}`, 'error');
      alert(`Test failed: ${error.message}`);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-4 md:p-8">
      <div className="max-w-6xl mx-auto">
        <header className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Authentication Debugger</h1>
          <p className="text-gray-600 dark:text-gray-400">
            Diagnose and troubleshoot authentication issues in your application
          </p>
        </header>

        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 mb-6">
          <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-6">
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white">Diagnostic Tests</h2>
              <div className="flex items-center gap-4 mt-2">
                <span className={`text-sm font-medium px-3 py-1 rounded-full ${errorCount === 0 ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300' : 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300'}`}>
                  {errorCount} {errorCount === 1 ? 'Error' : 'Errors'}
                </span>
                <span className="text-sm text-gray-500 dark:text-gray-400">
                  {tests.filter(t => t.status).length}/{tests.length} tests passed
                </span>
              </div>
            </div>

            <div className="flex flex-wrap gap-2">
              <div className="flex items-center gap-2">
                <label className="text-sm text-gray-600 dark:text-gray-400">Backend URL:</label>
                <input
                  type="text"
                  value={backendUrl}
                  onChange={(e) => setBackendUrl(e.target.value)}
                  className="px-3 py-1 text-sm border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-white w-48"
                  placeholder="http://localhost:5000"
                />
              </div>
              <button
                onClick={runTests}
                disabled={loading}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
              >
                <FaRedo className={loading ? 'animate-spin' : ''} />
                {loading ? 'Running...' : 'Run Tests'}
              </button>
            </div>
          </div>

          {loading ? (
            <div className="text-center py-12">
              <div className="w-16 h-16 border-4 border-blue-200 border-t-blue-600 rounded-full animate-spin mx-auto"></div>
              <p className="mt-6 text-gray-500 dark:text-gray-400">Running diagnostic tests...</p>
              <p className="text-sm text-gray-400 dark:text-gray-500 mt-2">
                This may take a few seconds
              </p>
            </div>
          ) : (
            <div className="space-y-4">
              {tests.map((test, index) => (
                <div
                  key={index}
                  className={`p-4 rounded-lg border transition-all duration-200 ${test.status ? 'bg-green-50 border-green-200 dark:bg-green-900/20 dark:border-green-800' : 'bg-red-50 border-red-200 dark:bg-red-900/20 dark:border-red-800'}`}
                >
                  <div className="flex flex-col md:flex-row md:items-center justify-between gap-3">
                    <div className="flex items-center gap-3">
                      {test.status ? (
                        <div className="w-6 h-6 rounded-full bg-green-100 dark:bg-green-900 flex items-center justify-center">
                          <FaCheck className="text-green-600 dark:text-green-400 text-sm" />
                        </div>
                      ) : (
                        <div className="w-6 h-6 rounded-full bg-red-100 dark:bg-red-900 flex items-center justify-center">
                          <FaTimes className="text-red-600 dark:text-red-400 text-sm" />
                        </div>
                      )}
                      <div>
                        <span className="font-medium text-gray-900 dark:text-white">{test.name}</span>
                        {test.name.includes('Note') && test.status === false && (
                          <span className="ml-3 text-xs px-2 py-1 bg-yellow-100 dark:bg-yellow-900 text-yellow-800 dark:text-yellow-300 rounded flex items-center gap-1">
                            <FaExclamationTriangle />
                            Response Issue
                          </span>
                        )}
                      </div>
                    </div>
                    <span className={`text-sm ${test.status ? 'text-green-700 dark:text-green-300' : 'text-red-700 dark:text-red-300'} font-medium`}>
                      {test.message}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="grid lg:grid-cols-2 gap-6 mb-8">
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 flex items-center gap-2">
              <FaKey className="text-gray-400" />
              Token & Debug Actions
            </h3>
            <div className="space-y-4">
              <div className="p-4 bg-gray-50 dark:bg-gray-900 rounded-lg">
                <p className="text-sm text-gray-500 dark:text-gray-400 mb-1">Token Status</p>
                <div className="flex items-center justify-between">
                  <p className={`font-medium ${localStorage.getItem('token') ? 'text-green-600 dark:text-green-400' : 'text-red-600 dark:text-red-400'}`}>
                    {localStorage.getItem('token') ? 'Present ✓' : 'Missing ✗'}
                  </p>
                  {localStorage.getItem('token') && (
                    <button
                      onClick={copyToken}
                      className="text-sm px-3 py-1 bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded hover:bg-gray-300 dark:hover:bg-gray-600"
                    >
                      Copy Token
                    </button>
                  )}
                </div>
              </div>

              <div className="space-y-3">
                <button
                  onClick={inspectResponse}
                  className="w-full py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors"
                >
                  Inspect Note Response
                </button>

                <button
                  onClick={clearToken}
                  className="w-full py-3 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                >
                  Clear Token Only
                </button>

                <button
                  onClick={() => window.location.href = '/login'}
                  className="w-full py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
                >
                  <FaUser />
                  Go to Login Page
                </button>

                <button
                  onClick={resetAuth}
                  className="w-full py-3 bg-red-700 text-white rounded-lg hover:bg-red-800 transition-colors"
                >
                  Reset Everything & Logout
                </button>
              </div>
            </div>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Debug Log</h3>
            <div className="h-64 overflow-y-auto bg-gray-900 text-gray-100 rounded-lg p-4 font-mono text-sm">
              {debugLog.length === 0 ? (
                <div className="text-gray-400 italic">No logs yet. Run tests to see debug information.</div>
              ) : (
                debugLog.map((log, index) => (
                  <div 
                    key={index} 
                    className={`mb-1 ${log.type === 'error' ? 'text-red-400' : log.type === 'warning' ? 'text-yellow-400' : 'text-green-400'}`}
                  >
                    {log.message}
                  </div>
                ))
              )}
            </div>
            <div className="mt-4 flex gap-2">
              <button
                onClick={() => setDebugLog([])}
                className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
              >
                Clear Log
              </button>
              <button
                onClick={() => {
                  console.log('Debug Log:', debugLog);
                  alert('Log copied to console');
                }}
                className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
              >
                Copy to Console
              </button>
            </div>
          </div>
        </div>

        <div className="bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-xl p-6">
          <h3 className="text-lg font-semibold text-yellow-800 dark:text-yellow-300 mb-4">
            Note Creation Issue Troubleshooting
          </h3>
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <h4 className="font-medium text-yellow-700 dark:text-yellow-400 mb-2">If noteId is undefined:</h4>
              <ul className="space-y-2 text-yellow-700 dark:text-yellow-400">
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Click <strong>"Inspect Note Response"</strong> to see the actual response structure</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Check backend API response - it might use <code className="bg-yellow-100 dark:bg-yellow-900 px-1 rounded">_id</code> instead of <code className="bg-yellow-100 dark:bg-yellow-900 px-1 rounded">id</code></span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Verify the note creation endpoint URL: <code className="bg-yellow-100 dark:bg-yellow-900 px-1 rounded">{backendUrl}/api/notes</code></span>
                </li>
              </ul>
            </div>

            <div>
              <h4 className="font-medium text-yellow-700 dark:text-yellow-400 mb-2">Common Backend Issues:</h4>
              <ul className="space-y-2 text-yellow-700 dark:text-yellow-400">
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Check if MongoDB/ObjectId is properly returning the ID field</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Verify the response format in your backend controller</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Ensure the note schema has the correct ID field name</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Check backend console for errors during note creation</span>
                </li>
              </ul>
            </div>
          </div>

          <div className="mt-6 pt-6 border-t border-yellow-200 dark:border-yellow-800">
            <div className="flex items-center gap-2 text-yellow-600 dark:text-yellow-500">
              <FaExclamationTriangle />
              <p className="text-sm">
                <strong>Debug Tip:</strong> Use the "Inspect Note Response" button to see the exact response structure from your backend.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DebugAuth;
