import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = {
  // Notes
  getNotes: () => axios.get(`${API_URL}/notes`),
  getNote: (id) => axios.get(`${API_URL}/notes/${id}`),
  createNote: (noteData) => axios.post(`${API_URL}/notes`, noteData),
  updateNote: (id, noteData) => axios.put(`${API_URL}/notes/${id}`, noteData),
  deleteNote: (id) => axios.delete(`${API_URL}/notes/${id}`),
  getNoteVersions: (id) => axios.get(`${API_URL}/notes/${id}/versions`),
  restoreVersion: (id, version) => axios.post(`${API_URL}/notes/${id}/restore/${version}`),

  // Sharing
  shareNote: (noteId, shareData) => axios.post(`${API_URL}/share/${noteId}`, shareData),
  getSharedNotes: () => axios.get(`${API_URL}/share/shared-with-me`),
  getShareKey: (noteId, fromUserId) => axios.get(`${API_URL}/share/key/${noteId}/${fromUserId}`),
  removeShare: (noteId, userId) => axios.delete(`${API_URL}/share/${noteId}/${userId}`),

  // Users
  searchUsers: (query) => axios.get(`${API_URL}/users/search?q=${query}`)
};

export default api;
