import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaCheck, FaTimes, FaKey, FaUser, FaRedo } from 'react-icons/fa';

const DebugAuth = () => {
  const [tests, setTests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errorCount, setErrorCount] = useState(0);
  const [backendUrl, setBackendUrl] = useState('http://localhost:5000');

  useEffect(() => {
    runTests();
  }, []);

  const runTests = async () => {
    setLoading(true);
    const testResults = [];
    let errors = 0;

    // Test 1: Check localStorage token
    const token = localStorage.getItem('token');
    const hasToken = !!token;
    if (!hasToken) errors++;

    testResults.push({
      name: 'Token in localStorage',
      status: hasToken,
      message: token ? `Token found (${token.length} chars)` : 'No token found'
    });

    // Test 2: Check backend health
    try {
      const healthResponse = await axios.get(`${backendUrl}/health`);
      testResults.push({
        name: 'Backend Health',
        status: healthResponse.status === 200,
        message: healthResponse.data.status
      });
    } catch (error) {
      errors++;
      testResults.push({
        name: 'Backend Health',
        status: false,
        message: `${error.message} - URL: ${backendUrl}`
      });
    }

    // Test 3: Validate token with backend
    if (token) {
      try {
        const authResponse = await axios.get(`${backendUrl}/api/auth/me`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        testResults.push({
          name: 'Token Validation',
          status: true,
          message: `Valid - User: ${authResponse.data.user?.email || 'Unknown'}`
        });
      } catch (error) {
        errors++;
        testResults.push({
          name: 'Token Validation',
          status: false,
          message: error.response?.data?.error || error.message || 'Unknown error'
        });
      }
    } else {
      errors++;
      testResults.push({
        name: 'Token Validation',
        status: false,
        message: 'No token to validate'
      });
    }

    // Test 4: Test note creation
    if (token) {
      try {
        const testNote = {
          title: 'Test Note',
          content: 'Test content',
          tags: ['test'],
          isEncrypted: false
        };

        const noteResponse = await axios.post(`${backendUrl}/api/notes`, testNote, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        testResults.push({
          name: 'Note Creation',
          status: true,
          message: `Created note ID: ${noteResponse.data.id}`
        });

        // Clean up
        await axios.delete(`${backendUrl}/api/notes/${noteResponse.data.id}`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        testResults.push({
          name: 'Note Cleanup',
          status: true,
          message: 'Test note cleaned up successfully'
        });
      } catch (error) {
        errors++;
        testResults.push({
          name: 'Note Creation',
          status: false,
          message: error.response?.data?.error || error.message || 'Failed to create note'
        });
      }
    } else {
      testResults.push({
        name: 'Note Creation',
        status: false,
        message: 'Skipped - No token'
      });
    }

    setTests(testResults);
    setErrorCount(errors);
    setLoading(false);
  };

  const clearToken = () => {
    localStorage.removeItem('token');
    window.location.reload();
  };

  const resetAuth = () => {
    localStorage.clear();
    sessionStorage.clear();
    window.location.href = '/login';
  };

  const copyToken = () => {
    const token = localStorage.getItem('token');
    if (token) {
      navigator.clipboard.writeText(token);
      alert('Token copied to clipboard!');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-4 md:p-8">
      <div className="max-w-6xl mx-auto">
        <header className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Authentication Debugger</h1>
          <p className="text-gray-600 dark:text-gray-400">
            Diagnose and troubleshoot authentication issues in your application
          </p>
        </header>

        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 mb-6">
          <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-6">
            <div>
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white">Diagnostic Tests</h2>
              <div className="flex items-center gap-4 mt-2">
                <span className={`text-sm font-medium px-3 py-1 rounded-full ${errorCount === 0 ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300' : 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300'}`}>
                  {errorCount} {errorCount === 1 ? 'Error' : 'Errors'}
                </span>
                <span className="text-sm text-gray-500 dark:text-gray-400">
                  {tests.filter(t => t.status).length}/{tests.length} tests passed
                </span>
              </div>
            </div>

            <div className="flex flex-wrap gap-2">
              <div className="flex items-center gap-2">
                <label className="text-sm text-gray-600 dark:text-gray-400">Backend URL:</label>
                <input
                  type="text"
                  value={backendUrl}
                  onChange={(e) => setBackendUrl(e.target.value)}
                  className="px-3 py-1 text-sm border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-white"
                  placeholder="http://localhost:5000"
                />
              </div>
              <button
                onClick={runTests}
                disabled={loading}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
              >
                <FaRedo className={loading ? 'animate-spin' : ''} />
                {loading ? 'Running...' : 'Run Tests'}
              </button>
            </div>
          </div>

          {loading ? (
            <div className="text-center py-12">
              <div className="w-16 h-16 border-4 border-blue-200 border-t-blue-600 rounded-full animate-spin mx-auto"></div>
              <p className="mt-6 text-gray-500 dark:text-gray-400">Running diagnostic tests...</p>
              <p className="text-sm text-gray-400 dark:text-gray-500 mt-2">
                This may take a few seconds
              </p>
            </div>
          ) : (
            <div className="space-y-4">
              {tests.map((test, index) => (
                <div
                  key={index}
                  className={`p-4 rounded-lg border transition-all duration-200 ${test.status ? 'bg-green-50 border-green-200 dark:bg-green-900/20 dark:border-green-800' : 'bg-red-50 border-red-200 dark:bg-red-900/20 dark:border-red-800'}`}
                >
                  <div className="flex flex-col md:flex-row md:items-center justify-between gap-3">
                    <div className="flex items-center gap-3">
                      {test.status ? (
                        <div className="w-6 h-6 rounded-full bg-green-100 dark:bg-green-900 flex items-center justify-center">
                          <FaCheck className="text-green-600 dark:text-green-400 text-sm" />
                        </div>
                      ) : (
                        <div className="w-6 h-6 rounded-full bg-red-100 dark:bg-red-900 flex items-center justify-center">
                          <FaTimes className="text-red-600 dark:text-red-400 text-sm" />
                        </div>
                      )}
                      <div>
                        <span className="font-medium text-gray-900 dark:text-white">{test.name}</span>
                        {test.status === false && (
                          <span className="ml-3 text-xs px-2 py-1 bg-red-100 dark:bg-red-900 text-red-800 dark:text-red-300 rounded">
                            Critical
                          </span>
                        )}
                      </div>
                    </div>
                    <span className={`text-sm ${test.status ? 'text-green-700 dark:text-green-300' : 'text-red-700 dark:text-red-300'} font-medium`}>
                      {test.message}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="grid lg:grid-cols-3 gap-6 mb-8">
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 flex items-center gap-2">
              <FaKey className="text-gray-400" />
              Token Management
            </h3>
            <div className="space-y-4">
              <div className="p-4 bg-gray-50 dark:bg-gray-900 rounded-lg">
                <p className="text-sm text-gray-500 dark:text-gray-400 mb-1">Token Status</p>
                <div className="flex items-center justify-between">
                  <p className={`font-medium ${localStorage.getItem('token') ? 'text-green-600 dark:text-green-400' : 'text-red-600 dark:text-red-400'}`}>
                    {localStorage.getItem('token') ? 'Present ✓' : 'Missing ✗'}
                  </p>
                  {localStorage.getItem('token') && (
                    <button
                      onClick={copyToken}
                      className="text-sm px-3 py-1 bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded hover:bg-gray-300 dark:hover:bg-gray-600"
                    >
                      Copy
                    </button>
                  )}
                </div>
              </div>

              {localStorage.getItem('token') && (
                <div className="p-4 bg-gray-100 dark:bg-gray-900 rounded-lg border border-gray-200 dark:border-gray-700">
                  <p className="text-xs font-mono break-all text-gray-700 dark:text-gray-300">
                    {localStorage.getItem('token').substring(0, 50)}...
                  </p>
                  <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
                    Full token available in console (localStorage.getItem('token'))
                  </p>
                </div>
              )}

              <button
                onClick={clearToken}
                className="w-full py-3 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
              >
                Clear Token Only
              </button>
            </div>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Quick Actions</h3>
            <div className="space-y-3">
              <button
                onClick={() => window.location.href = '/login'}
                className="w-full py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
              >
                <FaUser />
                Go to Login Page
              </button>

              <button
                onClick={resetAuth}
                className="w-full py-3 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
              >
                Reset Everything & Logout
              </button>

              <button
                onClick={() => {
                  localStorage.setItem('token', 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.test_invalid_token.example');
                  window.location.reload();
                }}
                className="w-full py-3 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700 transition-colors"
              >
                Set Invalid Token (Test)
              </button>

              <button
                onClick={() => {
                  localStorage.setItem('token', 'test_token_missing_user');
                  window.location.reload();
                }}
                className="w-full py-3 bg-orange-600 text-white rounded-lg hover:bg-orange-700 transition-colors"
              >
                Set Malformed Token
              </button>
            </div>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Diagnostic Info</h3>
            <div className="space-y-3">
              <div className="p-3 bg-gray-50 dark:bg-gray-900 rounded-lg">
                <p className="text-sm text-gray-500 dark:text-gray-400">Browser</p>
                <p className="font-medium">{navigator.userAgent.split(') ')[0].split('(')[1]}</p>
              </div>

              <div className="p-3 bg-gray-50 dark:bg-gray-900 rounded-lg">
                <p className="text-sm text-gray-500 dark:text-gray-400">Current Path</p>
                <p className="font-medium">{window.location.pathname}</p>
              </div>

              <div className="p-3 bg-gray-50 dark:bg-gray-900 rounded-lg">
                <p className="text-sm text-gray-500 dark:text-gray-400">LocalStorage</p>
                <p className="font-medium">{Object.keys(localStorage).length} items</p>
              </div>

              <button
                onClick={() => {
                  console.log('LocalStorage:', localStorage);
                  console.log('Current Token:', localStorage.getItem('token'));
                  alert('Check browser console for details');
                }}
                className="w-full py-3 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
              >
                Debug to Console
              </button>
            </div>
          </div>
        </div>

        <div className="bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-xl p-6">
          <h3 className="text-lg font-semibold text-yellow-800 dark:text-yellow-300 mb-4">
            Troubleshooting Guide
          </h3>
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <h4 className="font-medium text-yellow-700 dark:text-yellow-400 mb-2">Common Solutions</h4>
              <ul className="space-y-2 text-yellow-700 dark:text-yellow-400">
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span><strong>Clear localStorage</strong> and login again</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Check backend <code className="bg-yellow-100 dark:bg-yellow-900 px-1 rounded">JWT_SECRET</code> in backend/.env</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Ensure backend is running on correct port (default: 5000)</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Check browser console for CORS errors</span>
                </li>
              </ul>
            </div>

            <div>
              <h4 className="font-medium text-yellow-700 dark:text-yellow-400 mb-2">Advanced Steps</h4>
              <ul className="space-y-2 text-yellow-700 dark:text-yellow-400">
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Try incognito/private browsing mode</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Verify JWT token expiration</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Check backend logs for authentication errors</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Ensure CORS is properly configured on backend</span>
                </li>
              </ul>
            </div>
          </div>

          <div className="mt-6 pt-6 border-t border-yellow-200 dark:border-yellow-800">
