import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaPlus, FaSearch, FaLock, FaUnlock } from 'react-icons/fa';
import api from '../services/api';
import NoteList from '../components/NoteList';
import ShareModal from '../components/ShareModal';
import { useCrypto } from '../contexts/CryptoContext';
import { useAuth } from '../contexts/AuthContext'; 

import { decryptNote } from '../utils/cryptoUtils';

const Dashboard = () => {
  const [notes, setNotes] = useState([]);
  const [filteredNotes, setFilteredNotes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [shareModal, setShareModal] = useState(null);
  const [masterPassword, setMasterPassword] = useState('');
  const [showPasswordPrompt, setShowPasswordPrompt] = useState(false);
  const { masterKey, setMasterKey, deriveKeyFromPassword } = useCrypto();
   const { user, logout } = useAuth(); // Now only using it once
  const navigate = useNavigate();


  // In Dashboard.js, add this useEffect to check master key:
useEffect(() => {
  const checkMasterKey = () => {
    if (!masterKey) {
      // Check if user has a stored master key
      const storedKey = localStorage.getItem('masterKey');
      if (storedKey) {
        setMasterKey(storedKey);
      } else {
        // Prompt user to enter master password
        setShowPasswordPrompt(true);
      }
    }
  };

  if (user) {
    checkMasterKey();
  }
}, [user, masterKey, setMasterKey]);

  useEffect(() => {
    fetchNotes();
  }, []);

  useEffect(() => {
    if (searchTerm) {
      const filtered = notes.filter(note =>
        note.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        note.tags?.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
      );
      setFilteredNotes(filtered);
    } else {
      setFilteredNotes(notes);
    }
  }, [searchTerm, notes]);

  const fetchNotes = async () => {
    try {
      setLoading(true);
      const response = await api.getNotes();
      setNotes(response.data);
      setFilteredNotes(response.data);
    } catch (error) {
      console.error('Failed to fetch notes:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteNote = async (noteId) => {
    if (window.confirm('Are you sure you want to delete this note?')) {
      try {
        await api.deleteNote(noteId);
        fetchNotes();
      } catch (error) {
        console.error('Failed to delete note:', error);
        alert('Failed to delete note');
      }
    }
  };

  const handleShareNote = (note) => {
    if (!masterKey) {
      setShowPasswordPrompt(true);
      return;
    }
    setShareModal(note);
  };

  const handlePasswordSubmit = () => {
    // Derive master key from password
    const derivedKey = deriveKeyFromPassword(masterPassword, 'master-salt');
    setMasterKey(derivedKey);
    setShowPasswordPrompt(false);
    setMasterPassword('');
  };

  const isNoteDecrypted = (note) => {
    // Check if we have the key to decrypt this note
    return !!masterKey && note.isEncrypted;
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-gray-500">Loading notes...</div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900">My Notes</h1>
        <div className="flex space-x-4">
          <Link
            to="/note/new"
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 flex items-center"
          >
            <FaPlus className="mr-2" />
            New Note
          </Link>
          <Link
            to="/shared"
            className="px-4 py-2 border border-blue-600 text-blue-600 rounded-md hover:bg-blue-50"
          >
            Shared with Me
          </Link>
             <button
            onClick={() => {
              logout();
              navigate('/login');
            }}
            className="px-4 py-2 border border-red-600 text-red-600 rounded-md hover:bg-red-50"
          >
            Logout
          </button>
        </div>
      </div>

      <div className="mb-6">
        <div className="relative">
          <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
          <input
            type="text"
            placeholder="Search notes by title or tags..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
      </div>

      <div className="mb-4 flex justify-between items-center">
        <div className="text-sm text-gray-600">
          {filteredNotes.length} note{filteredNotes.length !== 1 ? 's' : ''} found
        </div>
        <div className="flex items-center space-x-2">
          {masterKey ? (
            <div className="flex items-center text-green-600">
              <FaLock className="mr-1" />
              <span className="text-sm">Encryption Active</span>
            </div>
          ) : (
            <button
              onClick={() => setShowPasswordPrompt(true)}
              className="flex items-center text-red-600 hover:text-red-700"
            >
              <FaUnlock className="mr-1" />
              <span className="text-sm">Unlock Encryption</span>
            </button>
          )}
        </div>
      </div>

      {filteredNotes.length === 0 ? (
        <div className="text-center py-12">
          <div className="text-gray-400 mb-4">No notes found</div>
          <Link
            to="/note/new"
            className="text-blue-600 hover:text-blue-700 font-medium"
          >
            Create your first note
          </Link>
        </div>
      ) : (
        <NoteList
          notes={filteredNotes}
          onDelete={handleDeleteNote}
          onShare={handleShareNote}
          isDecrypted={isNoteDecrypted}
        />
      )}

      {showPasswordPrompt && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">Enter Master Password</h3>
            <p className="text-sm text-gray-600 mb-4">
              Enter your master password to enable encryption/decryption features.
            </p>
            <input
              type="password"
              value={masterPassword}
              onChange={(e) => setMasterPassword(e.target.value)}
              placeholder="Master password"
              className="w-full px-3 py-2 border rounded-md mb-4"
            />
            <div className="flex justify-end space-x-3">
              <button
                onClick={() => setShowPasswordPrompt(false)}
                className="px-4 py-2 border rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handlePasswordSubmit}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                Unlock
              </button>
            </div>
          </div>
        </div>
      )}

      {shareModal && (
        <ShareModal
          note={shareModal}
          onClose={() => setShareModal(null)}
          onShare={fetchNotes}
        />
      )}
    </div>
  );
};

export default Dashboard;
