import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { FaSave, FaTimes, FaHistory, FaLock, FaKey } from 'react-icons/fa';
import api from '../services/api';
import { useCrypto } from '../contexts/CryptoContext';
import { encryptNote, decryptNote } from '../utils/cryptoUtils';

const NoteEditor = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isNewNote = id === 'new';

  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [tags, setTags] = useState('');
  const [loading, setLoading] = useState(!isNewNote);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);
  const [versions, setVersions] = useState([]);

  const { masterKey, encrypt, decrypt } = useCrypto();

  useEffect(() => {
    if (!isNewNote) {
      fetchNote();
    } else {
      setLoading(false);
    }
  }, [id]);



  const fetchVersions = async () => {
    try {
      const response = await api.getNoteVersions(id);
      setVersions(response.data.versions);
      setShowHistory(true);
    } catch (error) {
      console.error('Failed to fetch versions:', error);
    }
  };

  const handleSave = async () => {
    if (!title.trim()) {
      setError('Title is required');
      return;
    }

    setSaving(true);
    setError('');

    try {
      const noteData = {
        title: title.trim(),
        tags: tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      // Encrypt content if we have master key
      if (masterKey) {
        const encrypted = encryptNote(content, masterKey);
        noteData.content = encrypted.ciphertext;
        noteData.encryptionMetadata = {
          algorithm: 'AES-CBC',
          iv: encrypted.iv,
          salt: encrypted.salt
        };
      } else {
        noteData.content = content;
      }

      if (isNewNote) {
        const response = await api.createNote(noteData);
        navigate(`/note/${response.data.id}`);
      } else {
        await api.updateNote(id, noteData);
        alert('Note saved successfully!');
      }
    } catch (error) {
      console.error('Save error:', error);
      setError(error.response?.data?.error || 'Failed to save note');
    } finally {
      setSaving(false);
    }
  };

  const handleRestoreVersion = async (version) => {
    if (window.confirm(`Restore to version ${version}?`)) {
      try {
        await api.restoreVersion(id, version);
        fetchNote();
        setShowHistory(false);
        alert('Version restored successfully!');
      } catch (error) {
        console.error('Restore error:', error);
        alert('Failed to restore version');
      }
    }
  };

  const quillModules = {
    toolbar: [
      [{ 'header': [1, 2, 3, false] }],
      ['bold', 'italic', 'underline', 'strike'],
      [{ 'list': 'ordered'}, { 'list': 'bullet' }],
      ['link', 'image'],
      ['clean']
    ]
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-gray-500">Loading note...</div>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <div>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Note Title"
            className="text-3xl font-bold w-full border-0 focus:outline-none focus:ring-0"
          />
          <div className="mt-2">
            <input
              type="text"
              value={tags}
              onChange={(e) => setTags(e.target.value)}
              placeholder="Tags (comma separated)"
              className="text-sm text-gray-600 w-full border-0 focus:outline-none focus:ring-0"
            />
          </div>
        </div>
        <div className="flex space-x-3">
          {!isNewNote && (
            <button
              onClick={fetchVersions}
              className="px-4 py-2 border rounded-md hover:bg-gray-50 flex items-center"
            >
              <FaHistory className="mr-2" />
              History
            </button>
          )}
          <button
            onClick={() => navigate('/')}
            className="px-4 py-2 border rounded-md hover:bg-gray-50 flex items-center"
          >
            <FaTimes className="mr-2" />
            Cancel
          </button>
          <button
            onClick={handleSave}
            disabled={saving}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 flex items-center"
          >
            <FaSave className="mr-2" />
            {saving ? 'Saving...' : 'Save'}
          </button>
        </div>
      </div>

      {error && (
        <div className="mb-4 p-3 bg-red-50 text-red-600 rounded-md">
          {error}
        </div>
      )}

      <div className="mb-4 flex items-center justify-between">
        <div className="text-sm text-gray-600">
          {masterKey ? (
            <span className="text-green-600 flex items-center">
              <FaLock className="mr-1" />
              Content will be encrypted
            </span>
          ) : (
            <span className="text-red-600 flex items-center">
              <FaKey className="mr-1" />
              Content will NOT be encrypted
            </span>
          )}
        </div>
        <div className="text-sm text-gray-500">
          Characters: {content.length}
        </div>
      </div>

      <div className="border rounded-lg overflow-hidden">
        <ReactQuill
          value={content}
          onChange={setContent}
          modules={quillModules}
          className="h-96"
        />
      </div>

      {showHistory && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-2xl max-h-96 overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold">Note History</h3>
              <button
                onClick={() => setShowHistory(false)}
                className="text-gray-500 hover:text-gray-700"
              >
                <FaTimes />
              </button>
            </div>
            <div className="space-y-3">
              {versions.map((version) => (
                <div
                  key={version.version}
                  className="p-3 border rounded hover:bg-gray-50"
                >
                  <div className="flex justify-between items-center">
                    <div>
                      <div className="font-medium">Version {version.version}</div>
                      <div className="text-sm text-gray-500">
                        {new Date(version.savedAt).toLocaleString()}
                      </div>
                    </div>
                    <button
                      onClick={() => handleRestoreVersion(version.version)}
                      className="px-3 py-1 text-sm bg-blue-100 text-blue-600 rounded hover:bg-blue-200"
                    >
                      Restore
                    </button>
                  </div>
                  <div className="mt-2 text-sm text-gray-600 truncate">
                    {version.content.substring(0, 100)}...
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NoteEditor;
