import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  FaSave, FaTimes, FaHistory, FaLock, FaKey, 
  FaBold, FaItalic, FaUnderline, FaListUl, FaListOl,
  FaHeading, FaCode, FaLink, FaImage, FaQuoteLeft
} from 'react-icons/fa';
import api from '../services/api';
import { useCrypto } from '../contexts/CryptoContext';
import { useAuth } from '../contexts/AuthContext'; // ADD THIS IMPORT
import { encryptNote, decryptNote } from '../utils/cryptoUtils';

const NoteEditor = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isNewNote = id === 'new';

  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [tags, setTags] = useState('');
  const [loading, setLoading] = useState(!isNewNote);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);
  const [versions, setVersions] = useState([]);
  const [isAutoResizing, setIsAutoResizing] = useState(true);

  const textareaRef = useRef(null);
  const { masterKey, encrypt, decrypt } = useCrypto();
  const { user, isAuthenticated, logout } = useAuth(); // GET AUTH INFO

  useEffect(() => {
    // Check authentication
    if (!isAuthenticated) {
      setError('Please log in to create or edit notes');
      navigate('/login');
      return;
    }

    if (!isNewNote) {
      fetchNote();
    } else {
      setLoading(false);
    }
  }, [id, isAuthenticated, navigate]);

  // Auto-resize textarea based on content
  const resizeTextarea = () => {
    if (textareaRef.current && isAutoResizing) {
      textareaRef.current.style.height = 'auto';
      const newHeight = Math.max(400, textareaRef.current.scrollHeight);
      textareaRef.current.style.height = `${newHeight}px`;
    }
  };

  useEffect(() => {
    if (!isNewNote) {
      fetchNote();
    } else {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    resizeTextarea();
  }, [content]);

  const fetchNote = async () => {
    try {
      setLoading(true);
      const response = await api.getNote(id);
      const note = response.data;

      setTitle(note.title);
      setTags(note.tags?.join(', ') || '');

      // Decrypt content if we have the master key
      if (masterKey && note.isEncrypted && note.encryptionMetadata) {
        try {
          const decryptedContent = decryptNote(
            note.content,
            masterKey,
            note.encryptionMetadata.iv
          );
          setContent(decryptedContent);
        } catch (decryptError) {
          console.error('Decryption failed:', decryptError);
          setError('Failed to decrypt note. Check your master password.');
          setContent('[Encrypted - Enter master password to view]');
        }
      } else {
        setContent(note.content);
      }
    } catch (error) {
      console.error('Failed to fetch note:', error);
      setError('Failed to load note');
    } finally {
      setLoading(false);
    }
  };

  const fetchVersions = async () => {
    try {
      const response = await api.getNoteVersions(id);
      setVersions(response.data.versions);
      setShowHistory(true);
    } catch (error) {
      console.error('Failed to fetch versions:', error);
    }
  };

  const handleSave = async () => {
  if (!title.trim()) {
    setError('Title is required');
    return;
  }

  setSaving(true);
  setError('');

  try {
    const noteData = {
      title: title.trim(),
      tags: tags.split(',').map(tag => tag.trim()).filter(tag => tag),
      isEncrypted: !!masterKey // Add this flag
    };

    // Encrypt content if we have master key
    if (masterKey) {
      try {
        const encrypted = encryptNote(content, masterKey);
        noteData.content = encrypted.ciphertext;
        noteData.encryptionMetadata = {
          algorithm: 'AES-CBC',
          iv: encrypted.iv,
          salt: encrypted.salt
        };
        console.log('✅ Note will be encrypted');
      } catch (encryptError) {
        console.error('Encryption error:', encryptError);
        setError('Failed to encrypt note. Using plaintext.');
        noteData.content = content;
        noteData.isEncrypted = false;
      }
    } else {
      console.log('⚠️ No master key - saving as plaintext');
      noteData.content = content;
      noteData.isEncrypted = false;
    }

    console.log('Saving note:', {
      title: noteData.title,
      hasContent: !!noteData.content,
      isEncrypted: noteData.isEncrypted
    });

    if (isNewNote) {
      const response = await api.createNote(noteData);
      console.log('Note created:', response.data);
      navigate(`/note/${response.data.id}`);
    } else {
      const response = await api.updateNote(id, noteData);
      console.log('Note updated:', response.data);
      alert('Note saved successfully!');
    }
  } catch (error) {
    console.error('Save error details:', {
      message: error.message,
      response: error.response?.data,
      status: error.response?.status
    });

    const errorMsg = error.response?.data?.error || 
                    error.response?.data?.message || 
                    'Failed to save note';

    setError(`Server error: ${errorMsg}`);

    // If it's a network error
    if (error.message.includes('Network Error')) {
      setError('Cannot connect to server. Make sure backend is running on port 5000.');
    }
  } finally {
    setSaving(false);
  }
};

  const handleRestoreVersion = async (version) => {
    if (window.confirm(`Restore to version ${version}?`)) {
      try {
        await api.restoreVersion(id, version);
        fetchNote();
        setShowHistory(false);
        alert('Version restored successfully!');
      } catch (error) {
        console.error('Restore error:', error);
        alert('Failed to restore version');
      }
    }
  };

  // Enhanced formatting functions
  const formatText = (command, value = null) => {
    const textarea = textareaRef.current;
    if (!textarea) return;

    const start = textarea.selectionStart;
    const end = textarea.selectionEnd;
    const selectedText = content.substring(start, end);

    let formattedText = '';
    let cursorOffset = 0;

    switch (command) {
      case 'bold':
        formattedText = `**${selectedText}**`;
        cursorOffset = selectedText ? 2 : 0;
        break;
      case 'italic':
        formattedText = `*${selectedText}*`;
        cursorOffset = selectedText ? 1 : 0;
        break;
      case 'underline':
        formattedText = `<u>${selectedText}</u>`;
        cursorOffset = selectedText ? 3 : 0;
        break;
      case 'heading':
        const level = value || 1;
        formattedText = `${'#'.repeat(level)} ${selectedText}`;
        cursorOffset = selectedText ? level + 1 : 0;
        break;
      case 'bullet':
        formattedText = selectedText ? `\n- ${selectedText}` : `\n- `;
        cursorOffset = selectedText ? 3 : 2;
        break;
      case 'numbered':
        formattedText = selectedText ? `\n1. ${selectedText}` : `\n1. `;
        cursorOffset = selectedText ? 4 : 3;
        break;
      case 'code':
        formattedText = selectedText ? `\`\`\`\n${selectedText}\n\`\`\`` : `\`\`\`\n\`\`\``;
        cursorOffset = selectedText ? 4 : 4;
        break;
      case 'inlineCode':
        formattedText = `\`${selectedText}\``;
        cursorOffset = selectedText ? 1 : 0;
        break;
      case 'quote':
        formattedText = selectedText ? `> ${selectedText}` : `> `;
        cursorOffset = selectedText ? 2 : 2;
        break;
      case 'link':
        formattedText = `[${selectedText || 'link text'}](https://example.com)`;
        cursorOffset = selectedText ? 0 : 11; // Position cursor in URL
        break;
      case 'image':
        formattedText = `![${selectedText || 'alt text'}](https://example.com/image.jpg)`;
        cursorOffset = selectedText ? 0 : 28; // Position cursor in URL
        break;
      default:
        formattedText = selectedText;
    }

    const newContent = content.substring(0, start) + formattedText + content.substring(end);
    setContent(newContent);

    // Focus back on textarea and position cursor
    setTimeout(() => {
      textarea.focus();
      const newCursorPos = start + formattedText.length - (selectedText ? 0 : cursorOffset);
      textarea.setSelectionRange(newCursorPos, newCursorPos);
    }, 0);
  };

  // Handle keyboard shortcuts
  const handleKeyDown = (e) => {
    // Ctrl/Cmd + B for bold
    if ((e.ctrlKey || e.metaKey) && e.key === 'b') {
      e.preventDefault();
      formatText('bold');
    }
    // Ctrl/Cmd + I for italic
    if ((e.ctrlKey || e.metaKey) && e.key === 'i') {
      e.preventDefault();
      formatText('italic');
    }
    // Ctrl/Cmd + K for code
    if ((e.ctrlKey || e.metaKey) && e.key === 'k') {
      e.preventDefault();
      formatText('inlineCode');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-gray-500">Loading note...</div>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <div>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Note Title"
            className="text-3xl font-bold w-full border-0 focus:outline-none focus:ring-0 bg-transparent"
          />
          <div className="mt-2">
            <input
              type="text"
              value={tags}
              onChange={(e) => setTags(e.target.value)}
              placeholder="Tags (comma separated)"
              className="text-sm text-gray-600 w-full border-0 focus:outline-none focus:ring-0 bg-transparent"
            />
          </div>
        </div>
        <div className="flex space-x-3">
          {!isNewNote && (
            <button
              onClick={fetchVersions}
              className="px-4 py-2 border rounded-md hover:bg-gray-50 flex items-center transition-colors"
            >
              <FaHistory className="mr-2" />
              History
            </button>
          )}
          <button
            onClick={() => navigate('/')}
            className="px-4 py-2 border rounded-md hover:bg-gray-50 flex items-center transition-colors"
          >
            <FaTimes className="mr-2" />
            Cancel
          </button>
          <button
            onClick={handleSave}
            disabled={saving}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 flex items-center transition-colors"
          >
            <FaSave className="mr-2" />
            {saving ? 'Saving...' : 'Save'}
          </button>
        </div>
      </div>

      {error && (
        <div className="mb-4 p-3 bg-red-50 text-red-600 rounded-md">
          {error}
        </div>
      )}

      <div className="mb-4 flex items-center justify-between">
        <div className="text-sm text-gray-600">
          {masterKey ? (
            <span className="text-green-600 flex items-center">
              <FaLock className="mr-1" />
              Content will be encrypted
            </span>
          ) : (
            <span className="text-red-600 flex items-center">
              <FaKey className="mr-1" />
              Content will NOT be encrypted
            </span>
          )}
        </div>
        <div className="flex items-center space-x-4">
          <div className="text-sm text-gray-500">
            Characters: {content.length} | Words: {content.trim().split(/\s+/).filter(Boolean).length}
          </div>
          <label className="flex items-center text-sm text-gray-600">
            <input
              type="checkbox"
              checked={isAutoResizing}
              onChange={(e) => setIsAutoResizing(e.target.checked)}
              className="mr-2"
            />
            Auto-resize
          </label>
        </div>
      </div>

      {/* Enhanced formatting toolbar */}
      <div className="mb-4 p-3 bg-gray-50 rounded-lg border">
        <div className="flex flex-wrap gap-2 items-center">
          <span className="text-sm font-medium text-gray-700 mr-2">Format:</span>

          {/* Headings */}
          <div className="flex border-r pr-2">
            <button
              onClick={() => formatText('heading', 1)}
              className="px-3 py-2 border rounded hover:bg-white flex items-center"
              title="Heading 1"
            >
              <FaHeading className="mr-1" /> H1
            </button>
            <button
              onClick={() => formatText('heading', 2)}
              className="ml-1 px-3 py-2 border rounded hover:bg-white"
              title="Heading 2"
            >
              H2
            </button>
            <button
              onClick={() => formatText('heading', 3)}
              className="ml-1 px-3 py-2 border rounded hover:bg-white"
              title="Heading 3"
            >
              H3
            </button>
          </div>

          {/* Text formatting */}
          <div className="flex border-r pr-2">
            <button
              onClick={() => formatText('bold')}
              className="px-3 py-2 border rounded hover:bg-white"
              title="Bold (Ctrl+B)"
            >
              <FaBold />
            </button>
            <button
              onClick={() => formatText('italic')}
              className="ml-1 px-3 py-2 border rounded hover:bg-white"
              title="Italic (Ctrl+I)"
            >
              <FaItalic />
            </button>
            <button
              onClick={() => formatText('underline')}
              className="ml-1 px-3 py-2 border rounded hover:bg-white"
              title="Underline"
            >
              <FaUnderline />
            </button>
          </div>

          {/* Lists */}
          <div className="flex border-r pr-2">
            <button
              onClick={() => formatText('bullet')}
              className="px-3 py-2 border rounded hover:bg-white"
              title="Bullet List"
            >
              <FaListUl />
            </button>
            <button
              onClick={() => formatText('numbered')}
              className="ml-1 px-3 py-2 border rounded hover:bg-white"
              title="Numbered List"
            >
              <FaListOl />
            </button>
          </div>

          {/* Code & Quotes */}
          <div className="flex border-r pr-2">
            <button
              onClick={() => formatText('inlineCode')}
              className="px-3 py-2 border rounded hover:bg-white"
              title="Inline Code (Ctrl+K)"
            >
              <FaCode />
            </button>
            <button
              onClick={() => formatText('code')}
              className="ml-1 px-3 py-2 border rounded hover:bg-white"
              title="Code Block"
            >
              <FaCode className="text-lg" />
            </button>
            <button
              onClick={() => formatText('quote')}
              className="ml-1 px-3 py-2 border rounded hover:bg-white"
              title="Blockquote"
            >
              <FaQuoteLeft />
            </button>
          </div>

          {/* Links & Images */}
          <div className="flex">
            <button
              onClick={() => formatText('link')}
              className="px-3 py-2 border rounded hover:bg-white"
              title="Insert Link"
            >
              <FaLink />
            </button>
            <button
              onClick={() => formatText('image')}
              className="ml-1 px-3 py-2 border rounded hover:bg-white"
              title="Insert Image"
            >
              <FaImage />
            </button>
          </div>

          {/* Help text */}
          <div className="ml-4 text-xs text-gray-500">
            Supports full Markdown. Use keyboard shortcuts for faster editing.
          </div>
        </div>
      </div>

      {/* Large, resizable textarea for note content */}
      <div className="relative">
        <textarea
          ref={textareaRef}
          id="note-content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          onKeyDown={handleKeyDown}
          className="w-full min-h-[500px] border-2 border-gray-300 rounded-lg p-6 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 font-mono text-base resize-y transition-all"
          placeholder="Start typing your note here... Use Markdown for formatting: # Headings, **bold**, *italic*, `code`, - lists, etc."
          style={{
            lineHeight: '1.6',
            fontSize: '16px',
            minHeight: '500px',
            maxHeight: 'calc(100vh - 300px)'
          }}
        />

        {/* Character counter in corner */}
        <div className="absolute bottom-3 right-3 text-xs text-gray-400 bg-white/80 px-2 py-1 rounded">
          {content.length}/10000
        </div>
      </div>

      {/* Help section */}
      <div className="mt-4 p-4 bg-blue-50 rounded-lg border border-blue-200">
        <h4 className="font-medium text-blue-800 mb-2">Markdown Tips:</h4>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-3 text-sm">
          <div>
            <span className="font-mono"># Heading 1</span>
            <div className="text-gray-600">Large heading</div>
          </div>
          <div>
            <span className="font-mono">**bold**</span>
            <div className="text-gray-600">Bold text</div>
          </div>
          <div>
            <span className="font-mono">*italic*</span>
            <div className="text-gray-600">Italic text</div>
          </div>
          <div>
            <span className="font-mono">- item</span>
            <div className="text-gray-600">Bullet list</div>
          </div>
          <div>
            <span className="font-mono">1. item</span>
            <div className="text-gray-600">Numbered list</div>
          </div>
          <div>
            <span className="font-mono">`code`</span>
            <div className="text-gray-600">Inline code</div>
          </div>
          <div>
            <span className="font-mono">```code block```</span>
            <div className="text-gray-600">Code block</div>
          </div>
          <div>
            <span className="font-mono">[link](url)</span>
            <div className="text-gray-600">Hyperlink</div>
          </div>
        </div>
      </div>

      {/* History modal */}
      {showHistory && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg p-6 w-full max-w-3xl max-h-[80vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-semibold">Note History</h3>
              <button
                onClick={() => setShowHistory(false)}
                className="text-gray-500 hover:text-gray-700 p-2"
              >
                <FaTimes size={20} />
              </button>
            </div>
            <div className="space-y-4">
              {versions.map((version) => (
                <div
                  key={version.version}
                  className="p-4 border rounded-lg hover:bg-gray-50 transition-colors"
                >
                  <div className="flex justify-between items-center">
                    <div>
                      <div className="font-medium text-lg">Version {version.version}</div>
                      <div className="text-sm text-gray-500">
                        Saved: {new Date(version.savedAt).toLocaleString()}
                      </div>
                    </div>
                    <button
                      onClick={() => handleRestoreVersion(version.version)}
                      className="px-4 py-2 text-sm bg-blue-100 text-blue-600 rounded-lg hover:bg-blue-200 transition-colors"
                    >
                      Restore This Version
                    </button>
                  </div>
                  <div className="mt-3 text-sm text-gray-600 bg-gray-50 p-3 rounded font-mono overflow-auto max-h-32">
                    {version.content.substring(0, 500)}
                    {version.content.length > 500 && '...'}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NoteEditor;
