import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaCheck, FaTimes, FaKey, FaUser } from 'react-icons/fa';

const DebugAuth = () => {
  const [tests, setTests] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    runTests();
  }, []);

  const runTests = async () => {
    setLoading(true);
    const testResults = [];

    // Test 1: Check localStorage token
    const token = localStorage.getItem('token');
    testResults.push({
      name: 'Token in localStorage',
      status: !!token,
      message: token ? `Token found (${token.length} chars)` : 'No token found'
    });

    // Test 2: Check backend health
    try {
      const healthResponse = await axios.get('http://localhost:5000/health');
      testResults.push({
        name: 'Backend Health',
        status: healthResponse.status === 200,
        message: healthResponse.data.status
      });
    } catch (error) {
      testResults.push({
        name: 'Backend Health',
        status: false,
        message: error.message
      });
    }

    // Test 3: Validate token with backend
    if (token) {
      try {
        const authResponse = await axios.get('http://localhost:5000/api/auth/me', {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        testResults.push({
          name: 'Token Validation',
          status: true,
          message: `Valid - User: ${authResponse.data.user.email}`
        });
      } catch (error) {
        testResults.push({
          name: 'Token Validation',
          status: false,
          message: error.response?.data?.error || error.message
        });
      }
    } else {
      testResults.push({
        name: 'Token Validation',
        status: false,
        message: 'No token to validate'
      });
    }

    // Test 4: Test note creation
    if (token) {
      try {
        const testNote = {
          title: 'Test Note',
          content: 'Test content',
          tags: ['test'],
          isEncrypted: false
        };

        const noteResponse = await axios.post('http://localhost:5000/api/notes', testNote, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        testResults.push({
          name: 'Note Creation',
          status: true,
          message: `Created note ID: ${noteResponse.data.id}`
        });

        // Clean up
        await axios.delete(`http://localhost:5000/api/notes/${noteResponse.data.id}`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
      } catch (error) {
        testResults.push({
          name: 'Note Creation',
          status: false,
          message: error.response?.data?.error || error.message
        });
      }
    }

    setTests(testResults);
    setLoading(false);
  };

  const clearToken = () => {
    localStorage.removeItem('token');
    window.location.reload();
  };

  const resetAuth = () => {
    localStorage.clear();
    window.location.href = '/login';
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-6">Authentication Debugger</h1>

        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 mb-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white">Diagnostic Tests</h2>
            <button
              onClick={runTests}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              Run Tests Again
            </button>
          </div>

          {loading ? (
            <div className="text-center py-8">
              <div className="w-12 h-12 border-4 border-blue-200 border-t-blue-600 rounded-full animate-spin mx-auto"></div>
              <p className="mt-4 text-gray-500">Running tests...</p>
            </div>
          ) : (
            <div className="space-y-4">
              {tests.map((test, index) => (
                <div
                  key={index}
                  className={`p-4 rounded-lg border ${test.status ? 'bg-green-50 border-green-200 dark:bg-green-900/20 dark:border-green-800' : 'bg-red-50 border-red-200 dark:bg-red-900/20 dark:border-red-800'}`}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      {test.status ? (
                        <FaCheck className="text-green-600 dark:text-green-400" />
                      ) : (
                        <FaTimes className="text-red-600 dark:text-red-400" />
                      )}
                      <span className="font-medium text-gray-900 dark:text-white">{test.name}</span>
                    </div>
                    <span className={`text-sm ${test.status ? 'text-green-700 dark:text-green-300' : 'text-red-700 dark:text-red-300'}`}>
                      {test.message}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="grid md:grid-cols-2 gap-6">
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Token Information</h3>
            <div className="space-y-3">
              <div className="flex items-center gap-3">
                <FaKey className="text-gray-400" />
                <div>
                  <p className="text-sm text-gray-500">Token Status</p>
                  <p className="font-medium">
                    {localStorage.getItem('token') ? 'Present' : 'Missing'}
                  </p>
                </div>
              </div>
              {localStorage.getItem('token') && (
                <div className="p-3 bg-gray-100 dark:bg-gray-900 rounded-lg">
                  <p className="text-xs font-mono break-all">
                    {localStorage.getItem('token')}
                  </p>
                </div>
              )}
              <button
                onClick={clearToken}
                className="w-full py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
              >
                Clear Token
              </button>
            </div>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Quick Actions</h3>
            <div className="space-y-3">
              <button
                onClick={() => window.location.href = '/login'}
                className="w-full py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center justify-center gap-2"
              >
                <FaUser />
                Go to Login
              </button>
              <button
                onClick={resetAuth}
                className="w-full py-3 bg-red-600 text-white rounded-lg hover:bg-red-700"
              >
                Reset Everything & Logout
              </button>
              <button
                onClick={() => {
                  localStorage.setItem('token', 'test_invalid_token');
                  window.location.reload();
                }}
                className="w-full py-3 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700"
              >
                Set Invalid Token (Test)
              </button>
            </div>
          </div>
        </div>

        <div className="mt-8 bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-xl p-6">
          <h3 className="text-lg font-semibold text-yellow-800 dark:text-yellow-300 mb-3">Common Solutions</h3>
          <ul className="space-y-2 text-yellow-700 dark:text-yellow-400">
            <li>1. <strong>Clear localStorage</strong> and login again</li>
            <li>2. Check if backend JWT_SECRET is set (look in backend/.env)</li>
            <li>3. Make sure backend is running on port 5000</li>
            <li>4. Check browser console for CORS errors</li>
            <li>5. Try a different browser or incognito mode</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default DebugAuth;
