import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DebugAuth = () => {
  const [backendUrl, setBackendUrl] = useState('http://localhost:5000');
  const [results, setResults] = useState({});
  const [loading, setLoading] = useState(false);
  const [exactFix, setExactFix] = useState('');

  const testNoteCreation = async () => {
    setLoading(true);
    setResults({});
    setExactFix('');

    const token = localStorage.getItem('token');

    if (!token) {
      setResults({ error: 'No token found in localStorage' });
      setLoading(false);
      return;
    }

    console.clear();
    console.log('=== STARTING NOTE CREATION DEBUG ===');
    console.log('Token exists:', !!token);
    console.log('Backend URL:', backendUrl);

    try {
      // Test 1: Create a note with minimal data
      const noteData = {
        title: 'Debug Test Note',
        content: 'Test content',
        tags: ['debug']
      };

      console.log('\n=== SENDING REQUEST ===');
      console.log('Endpoint:', `${backendUrl}/api/notes`);
      console.log('Data:', noteData);

      const response = await axios.post(`${backendUrl}/api/notes`, noteData, {
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('\n=== RESPONSE RECEIVED ===');
      console.log('Status:', response.status);
      console.log('Full response:', response);
      console.log('Response data:', response.data);
      console.log('Type of response.data:', typeof response.data);

      if (response.data) {
        console.log('Keys in response.data:', Object.keys(response.data));

        // Check for ID in every possible location
        const findIds = (obj, path = '') => {
          for (const key in obj) {
            const currentPath = path ? `${path}.${key}` : key;
            const value = obj[key];

            if (key.toLowerCase().includes('id') && value) {
              console.log(`‚úÖ Found ID at: ${currentPath} = ${value}`);
            }

            if (typeof value === 'object' && value !== null) {
              findIds(value, currentPath);
            }
          }
        };

        findIds(response.data);
      }

      setResults({
        success: true,
        status: response.status,
        data: response.data,
        idFound: response.data?.id || response.data?._id || response.data?.note?.id
      });

      // Generate exact fix code
      let fixCode = '';
      if (response.data?.id) {
        fixCode = `// ‚úÖ Your backend returns: response.data.id
const response = await axios.post('/api/notes', noteData, {
  headers: { 'Authorization': \`Bearer \${token}\` }
});

// Extract ID like this:
const noteId = response.data.id;

console.log('Note created with ID:', noteId); // Should show actual ID

// Use noteId for further operations
if (!noteId) {
  throw new Error('Note created but no ID returned');
}`;
      } else if (response.data?._id) {
        fixCode = `// ‚úÖ Your backend returns: response.data._id
const response = await axios.post('/api/notes', noteData, {
  headers: { 'Authorization': \`Bearer \${token}\` }
});

// Extract ID like this:
const noteId = response.data._id; // Note the underscore!

console.log('Note created with ID:', noteId); // Should show actual ID

// Use noteId for further operations
if (!noteId) {
  throw new Error('Note created but no ID returned');
}`;
      } else if (response.data?.note?.id) {
        fixCode = `// ‚úÖ Your backend returns: response.data.note.id
const response = await axios.post('/api/notes', noteData, {
  headers: { 'Authorization': \`Bearer \${token}\` }
});

// Extract ID like this:
const noteId = response.data.note.id; // Nested in note object!

console.log('Note created with ID:', noteId); // Should show actual ID

// Use noteId for further operations
if (!noteId) {
  throw new Error('Note created but no ID returned');
}`;
      } else {
        fixCode = `// ‚ùå NO ID FOUND in response!
const response = await axios.post('/api/notes', noteData, {
  headers: { 'Authorization': \`Bearer \${token}\` }
});

console.log('Full response:', response.data); // Check what's actually returned

// Your backend is not returning an ID. Check backend code.
// Likely issue: Backend returns { message: '...' } without note ID
const noteId = response.data.id || response.data._id || response.data.note?.id;

if (!noteId) {
  console.error('Backend response:', response.data);
  throw new Error('Backend created note but did not return an ID');
}`;
      }

      setExactFix(fixCode);

      // If we got an ID, test retrieving it
      const noteId = response.data?.id || response.data?._id || response.data?.note?.id;
      if (noteId) {
        console.log('\n=== TESTING NOTE RETRIEVAL ===');
        try {
          const getResponse = await axios.get(`${backendUrl}/api/notes/${noteId}`, {
            headers: { 'Authorization': `Bearer ${token}` }
          });
          console.log('‚úÖ Can retrieve note:', getResponse.data);
        } catch (getError) {
          console.log('‚ùå Cannot retrieve note:', getError.message);
          if (getError.response?.data?.error?.includes('ObjectId')) {
            setExactFix(prev => prev + `\n\n// ‚ö†Ô∏è WARNING: Backend rejects ID "${noteId}" as invalid ObjectId\n// Check if this is a valid MongoDB ObjectId (24 hex characters)`);
          }
        }
      }

    } catch (error) {
      console.log('\n=== REQUEST FAILED ===');
      console.log('Error:', error.message);
      console.log('Error response:', error.response?.data);
      console.log('Status:', error.response?.status);

      setResults({
        error: true,
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });

      // Generate fix based on error
      let errorFix = '';
      if (error.response?.data?.error?.includes('ObjectId') || error.response?.data?.error?.includes('24-character')) {
        errorFix = `// ‚ùå ERROR: "${error.response.data.error}"
// This means your frontend is passing undefined or invalid ID to backend.

// COMMON CAUSES & FIXES:
// 1. Note creation not returning ID:
const response = await axios.post('/api/notes', noteData);
console.log('Response:', response.data); // Check what's returned

// 2. Wrong ID field name:
const noteId = response.data.id || response.data._id || response.data.note?.id;
if (!noteId) {
  console.error('No ID found in:', response.data);
  throw new Error('Note created but no ID returned');
}

// 3. Using undefined variable:
// Make sure noteId is defined before using it:
if (!noteId || noteId === 'undefined') {
  throw new Error('Invalid note ID: ' + noteId);
}`;
      } else {
        errorFix = `// ‚ùå ERROR: ${error.message}
// Status: ${error.response?.status}

// Check:
// 1. Is backend running? ${backendUrl}
// 2. Is token valid?
// 3. Check browser console (F12) for more details`;
      }

      setExactFix(errorFix);
    }

    console.log('\n=== DEBUG COMPLETE ===');
    setLoading(false);
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    alert('Copied to clipboard!');
  };

  const applyEmergencyFix = () => {
    // This is a brute force fix for the "undefined" ObjectId error
    const emergencyFix = `
// EMERGENCY FIX for "Invalid note ID format: 'undefined'"

// In your note creation function, FIND where you create notes:
// Look for: axios.post('/api/notes', ...)

// REPLACE the ID extraction with this:

async function createNote(noteData) {
  try {
    // 1. Create note
    const response = await axios.post('/api/notes', noteData, {
      headers: { 'Authorization': \`Bearer \${localStorage.getItem('token')}\` }
    });

    console.log('üîç Note creation response:', response.data);

    // 2. Extract ID - TRY ALL POSSIBLE LOCATIONS
    let noteId = null;

    if (response.data.id) {
      noteId = response.data.id;
      console.log('‚úÖ Found ID at: response.data.id');
    } else if (response.data._id) {
      noteId = response.data._id;
      console.log('‚úÖ Found ID at: response.data._id');
    } else if (response.data.note && response.data.note.id) {
      noteId = response.data.note.id;
      console.log('‚úÖ Found ID at: response.data.note.id');
    } else if (response.data.note && response.data.note._id) {
      noteId = response.data.note._id;
      console.log('‚úÖ Found ID at: response.data.note._id');
    } else {
      console.error('‚ùå NO ID FOUND in response!', response.data);
      throw new Error('Note created but no ID returned. Response: ' + JSON.stringify(response.data));
    }

    // 3. VALIDATE ID before using it
    if (!noteId || noteId === 'undefined' || noteId === 'null') {
      console.error('Invalid noteId:', noteId);
      throw new Error('Invalid note ID: ' + noteId);
    }

    console.log('‚úÖ Note ID:', noteId);
    return noteId;

  } catch (error) {
    console.error('Note creation failed:', error);
    throw error;
  }
}

// USE IT LIKE THIS:
// const noteId = await createNote({ title: '...', content: '...' });
// Then use noteId for any further operations
    `;

    navigator.clipboard.writeText(emergencyFix);
    alert('Emergency fix copied! Find your note creation function and replace the ID extraction with this code.');
  };

  return (
    <div style={{ 
      padding: '20px', 
      backgroundColor: '#0f172a', 
      minHeight: '100vh',
      color: 'white',
      fontFamily: 'system-ui, -apple-system, sans-serif'
    }}>
      <h1 style={{ color: '#f87171', marginBottom: '10px' }}>
        üö® URGENT: Note ID Debugger
      </h1>
      <p style={{ color: '#94a3b8', marginBottom: '30px' }}>
        Fixing: "Invalid note ID format: 'undefined'"
      </p>

      <div style={{ 
        backgroundColor: '#1e293b', 
        padding: '20px', 
        borderRadius: '10px',
        marginBottom: '20px'
      }}>
        <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
          <input
            type="text"
            value={backendUrl}
            onChange={(e) => setBackendUrl(e.target.value)}
            style={{
              flex: 1,
              padding: '10px',
              backgroundColor: '#334155',
              border: '1px solid #475569',
              borderRadius: '6px',
              color: 'white'
            }}
            placeholder="Backend URL"
          />
          <button
            onClick={testNoteCreation}
            disabled={loading}
            style={{
              padding: '10px 20px',
              backgroundColor: loading ? '#475569' : '#dc2626',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              fontWeight: 'bold',
              cursor: loading ? 'not-allowed' : 'pointer'
            }}
          >
            {loading ? 'üîÑ Testing...' : 'üîç Debug Note Creation'}
          </button>
        </div>

        <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
          <button
            onClick={applyEmergencyFix}
            style={{
              padding: '10px 15px',
              backgroundColor: '#059669',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer'
            }}
          >
            ‚ö° Apply Emergency Fix
          </button>
          <button
            onClick={() => copyToClipboard(exactFix)}
            disabled={!exactFix}
            style={{
              padding: '10px 15px',
              backgroundColor: !exactFix ? '#475569' : '#2563eb',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: !exactFix ? 'not-allowed' : 'pointer'
            }}
          >
            üìã Copy Fix Code
          </button>
          <button
            onClick={() => {
              localStorage.clear();
              window.location.reload();
            }}
            style={{
              padding: '10px 15px',
              backgroundColor: '#7c3aed',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer'
            }}
          >
            üóëÔ∏è Clear & Refresh
          </button>
        </div>
      </div>

      {results.error && (
        <div style={{ 
          backgroundColor: '#7f1d1d', 
          padding: '20px', 
          borderRadius: '10px',
          marginBottom: '20px',
          border: '2px solid #ef4444'
        }}>
          <h3 style={{ color: '#fca5a5', marginTop: 0 }}>‚ùå Error Detected</h3>
          <p><strong>Message:</strong> {results.message}</p>
          <p><strong>Status:</strong> {results.status}</p>
          {results.response && (
            <div>
              <p><strong>Response:</strong></p>
              <pre style={{ 
                backgroundColor: '#000', 
                padding: '10px', 
                borderRadius: '5px',
                overflow: 'auto',
                fontSize: '12px'
              }}>
                {JSON.stringify(results.response, null, 2)}
              </pre>
            </div>
          )}
        </div>
      )}

      {results.success && (
        <div style={{ 
          backgroundColor: '#064e3b', 
          padding: '20px', 
          borderRadius: '10px',
          marginBottom: '20px',
          border: '2px solid #10b981'
        }}>
          <h3 style={{ color: '#a7f3d0', marginTop: 0 }}>‚úÖ Note Created Successfully</h3>
          <p><strong>Status:</strong> {results.status}</p>
          <p><strong>ID Found:</strong> {results.idFound ? 'Yes' : 'No'}</p>
          <div>
            <p><strong>Response Data:</strong></p>
            <pre style={{ 
              backgroundColor: '#000', 
              padding: '10px', 
              borderRadius: '5px',
              overflow: 'auto',
              fontSize: '12px'
            }}>
              {JSON.stringify(results.data, null, 2)}
            </pre>
          </div>
        </div>
      )}

      {exactFix && (
        <div style={{ 
          backgroundColor: '#1e3a8a', 
          padding: '20px', 
          borderRadius: '10px',
          marginBottom: '20px'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
            <h3 style={{ color: '#93c5fd', marginTop: 0 }}>üîß Exact Fix Required</h3>
            <button
              onClick={() => copyToClipboard(exactFix)}
              style={{
                padding: '5px 10px',
                backgroundColor: '#2563eb',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                fontSize: '12px',
                cursor: 'pointer'
              }}
            >
              Copy
            </button>
          </div>
          <pre style={{ 
            backgroundColor: '#000', 
            padding: '15px', 
            borderRadius: '5px',
            overflow: 'auto',
            fontSize: '12px',
            color: '#cbd5e1',
            lineHeight: '1.5'
          }}>
            {exactFix}
          </pre>
        </div>
      )}

      <div style={{ 
        backgroundColor: '#78350f', 
        padding: '20px', 
        borderRadius: '10px'
      }}>
        <h3 style={{ color: '#fbbf24', marginTop: 0 }}>üéØ Immediate Action Steps</h3>
        <ol style={{ lineHeight: '2', color: '#fde68a' }}>
          <li>
            <strong>Click "üîç Debug Note Creation"</strong> - This will create a test note and show you EXACTLY what the backend returns
          </li>
          <li>
            <strong>Open Browser Console (F12)</strong> - Look for "STARTING NOTE CREATION DEBUG" section
          </li>
          <li>
            <strong>Check what ID field exists</strong> - Look in console for "Found ID at:"
          </li>
          <li>
            <strong>Click "‚ö° Apply Emergency Fix"</strong> - Get the code that handles ALL possible ID locations
          </li>
          <li>
            <strong>Find your frontend's note creation code</strong> - Search for "axios.post('/api/notes'" in your codebase
          </li>
          <li>
            <strong>Replace the ID extraction</strong> - Use the emergency fix code
          </li>
        </ol>

        <div style={{ 
          marginTop: '20px', 
          padding: '15px', 
          backgroundColor: '#000', 
          borderRadius: '5px',
          border: '1px solid #f59e0b'
        }}>
          <p style={{ color: '#fbbf24', margin: 0 }}>
            <strong>üí° PRO TIP:</strong> The error happens because somewhere in your code, you're trying to use 
            <code style={{ backgroundColor: '#333', padding: '2px 5px', margin: '0 5px' }}>undefined</code> 
            as a note ID. Find where notes are created and make sure you extract the ID correctly.
          </p>
        </div>
      </div>

      <div style={{ 
        marginTop: '20px', 
        padding: '15px', 
        backgroundColor: '#312e81', 
        borderRadius: '10px',
        fontSize: '14px'
      }}>
        <h4 style={{ color: '#a5b4fc', marginTop: 0 }}>üìñ Understanding Your Backend</h4>
        <p style={{ color: '#c7d2fe' }}>
          Based on your code, your backend has <strong>two different response formats</strong>:
        </p>
        <ul style={{ color: '#c7d2fe', lineHeight: '1.6' }}>
          <li><code>routes/notes.js</code> returns: <code>{`{ id: "...", title: "...", createdAt: "..." }`}</code></li>
          <li><code>noteController.js</code> returns: <code>{`{ message: "...", note: { id: "...", title: "...", createdAt: "..." } }`}</code></li>
        </ul>
        <p style={{ color: '#c7d2fe' }}>
