import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaCheck, FaTimes, FaKey, FaUser, FaRedo, FaExclamationTriangle, FaBug, FaDatabase, FaServer } from 'react-icons/fa';

const DebugAuth = () => {
  const [tests, setTests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errorCount, setErrorCount] = useState(0);
  const [backendUrl, setBackendUrl] = useState('http://localhost:5000');
  const [debugLog, setDebugLog] = useState([]);
  const [specificError, setSpecificError] = useState('');

  useEffect(() => {
    runTests();
  }, []);

  const addLog = (message, type = 'info') => {
    const timestamp = new Date().toLocaleTimeString();
    const logEntry = { message: `[${timestamp}] ${message}`, type };
    setDebugLog(prev => [...prev, logEntry]);
    console.log(`[${type.toUpperCase()}] ${message}`);
  };

  const runTests = async () => {
    setLoading(true);
    setDebugLog([]);
    setSpecificError('');
    const testResults = [];
    let errors = 0;

    addLog('Starting authentication debug tests...', 'info');

    // Test 1: Check localStorage token
    const token = localStorage.getItem('token');
    const hasToken = !!token;

    if (!hasToken) errors++;

    addLog(`Token check: ${hasToken ? 'Token found' : 'No token found'}`, hasToken ? 'info' : 'error');

    testResults.push({
      name: 'Token in localStorage',
      status: hasToken,
      message: token ? `Token found (${token.length} chars)` : 'No token found'
    });

    // Test 2: Check backend health
    try {
      addLog(`Testing backend health at: ${backendUrl}/health`, 'info');
      const healthResponse = await axios.get(`${backendUrl}/health`, {
        timeout: 5000
      });

      addLog(`Backend health: ${healthResponse.status} - ${JSON.stringify(healthResponse.data)}`, 'info');

      testResults.push({
        name: 'Backend Health',
        status: healthResponse.status === 200,
        message: healthResponse.data?.status || `Status: ${healthResponse.status}`
      });
    } catch (error) {
      errors++;
      const errorMsg = `Backend connection failed: ${error.message}`;
      addLog(errorMsg, 'error');
      setSpecificError(`Cannot connect to backend at ${backendUrl}. Make sure backend is running.`);

      testResults.push({
        name: 'Backend Health',
        status: false,
        message: errorMsg
      });
      setTests(testResults);
      setErrorCount(errors);
      setLoading(false);
      return; // Stop further tests if backend is down
    }

    // Test 3: Validate token with backend
    if (token) {
      try {
        addLog('Validating token with /api/auth/me endpoint...', 'info');
        const authResponse = await axios.get(`${backendUrl}/api/auth/me`, {
          headers: { 'Authorization': `Bearer ${token}` },
          timeout: 5000
        });

        addLog(`Auth response received: ${JSON.stringify(authResponse.data)}`, 'info');

        // Extract user info from various possible response formats
        const userData = authResponse.data.user || authResponse.data;
        const userEmail = userData.email || 'Unknown';
        const userId = userData.id || userData._id || 'No ID';

        addLog(`User authenticated: ${userEmail} (ID: ${userId})`, 'success');

        testResults.push({
          name: 'Token Validation',
          status: true,
          message: `User: ${userEmail}`
        });
      } catch (error) {
        errors++;
        const errorMsg = error.response?.data?.error || error.message;
        addLog(`Token validation failed: ${errorMsg}`, 'error');

        if (error.response?.status === 401) {
          setSpecificError('Token is invalid or expired. Clear token and login again.');
        }

        testResults.push({
          name: 'Token Validation',
          status: false,
          message: `Failed: ${errorMsg}`
        });
      }
    } else {
      errors++;
      addLog('No token available for validation', 'warning');
      testResults.push({
        name: 'Token Validation',
        status: false,
        message: 'No token to validate'
      });
    }

    // Test 4: DIRECT FIX - Test note creation with proper ID handling
    if (token) {
      try {
        addLog('Testing note creation to identify ID format issue...', 'info');

        // Create a test note
        const testNote = {
          title: 'Debug Test Note',
          content: 'This note was created by the debugger to test ID handling.',
          tags: ['debug', 'test']
        };

        addLog(`Creating note with data: ${JSON.stringify(testNote)}`, 'info');

        const createResponse = await axios.post(`${backendUrl}/api/notes`, testNote, {
          headers: { 'Authorization': `Bearer ${token}` },
          timeout: 10000
        });

        addLog(`Note creation response: ${JSON.stringify(createResponse.data)}`, 'info');

        // CRITICAL: Check where the ID is in the response
        const responseData = createResponse.data;
        let noteId = null;
        let idLocation = 'unknown';

        // Try all possible ID locations
        if (responseData.id) {
          noteId = responseData.id;
          idLocation = 'response.data.id';
        } else if (responseData._id) {
          noteId = responseData._id;
          idLocation = 'response.data._id';
          setSpecificError(`Backend returns ID as '_id' not 'id'. Frontend needs to use response.data._id`);
        } else if (responseData.note?.id) {
          noteId = responseData.note.id;
          idLocation = 'response.data.note.id';
        } else if (responseData.note?._id) {
          noteId = responseData.note._id;
          idLocation = 'response.data.note._id';
        } else if (responseData.data?.id) {
          noteId = responseData.data.id;
          idLocation = 'response.data.data.id';
        } else if (responseData.data?._id) {
          noteId = responseData.data._id;
          idLocation = 'response.data.data._id';
        }

        if (noteId) {
          addLog(`âœ“ Note created successfully! ID found at: ${idLocation}`, 'success');
          addLog(`Note ID: ${noteId}`, 'success');

          // Check if it's a valid ObjectId
          const isObjectId = /^[0-9a-fA-F]{24}$/.test(noteId);
          addLog(`ID Format: ${isObjectId ? 'Valid 24-char ObjectId' : 'Not a standard ObjectId'}`, isObjectId ? 'success' : 'warning');

          if (!isObjectId) {
            setSpecificError(`Note ID "${noteId}" is not a standard MongoDB ObjectId. Backend may be using custom IDs.`);
          }

          testResults.push({
            name: 'Note Creation',
            status: true,
            message: `Created - ID at ${idLocation}: ${noteId.substring(0, 20)}...`
          });

          // Test getting the note by ID
          addLog(`Testing GET note by ID: /api/notes/${noteId}`, 'info');
          try {
            const getResponse = await axios.get(`${backendUrl}/api/notes/${noteId}`, {
              headers: { 'Authorization': `Bearer ${token}` }
            });

            addLog(`GET note response: ${JSON.stringify(getResponse.data)}`, 'info');

            testResults.push({
              name: 'Note Retrieval',
              status: true,
              message: 'Can retrieve note by ID'
            });
          } catch (getError) {
            errors++;
            const getErrorMsg = getError.response?.data?.error || getError.message;
            addLog(`GET note failed: ${getErrorMsg}`, 'error');

            if (getErrorMsg.includes('ObjectId') || getErrorMsg.includes('24-character')) {
              setSpecificError(`Backend rejects ID "${noteId}" as invalid ObjectId. ID format mismatch.`);
            }

            testResults.push({
              name: 'Note Retrieval',
              status: false,
              message: `Failed: ${getErrorMsg}`
            });
          }

          // Clean up - delete the test note
          addLog(`Cleaning up test note (ID: ${noteId})`, 'info');
          try {
            await axios.delete(`${backendUrl}/api/notes/${noteId}`, {
              headers: { 'Authorization': `Bearer ${token}` }
            });
            addLog('Test note deleted successfully', 'success');

            testResults.push({
              name: 'Note Cleanup',
              status: true,
              message: 'Test note removed'
            });
          } catch (deleteError) {
            addLog(`Note cleanup failed (non-critical): ${deleteError.message}`, 'warning');
            testResults.push({
              name: 'Note Cleanup',
              status: false,
              message: 'Could not delete test note'
            });
          }
        } else {
          errors++;
          addLog('Note created but NO ID found in response!', 'error');
          addLog(`Response keys: ${Object.keys(responseData).join(', ')}`, 'error');
          setSpecificError('Backend creates notes but does not return an ID in the response. Check backend response format.');

          testResults.push({
            name: 'Note Creation',
            status: false,
            message: 'Created but no ID returned'
          });
        }
      } catch (error) {
        errors++;
        const errorMsg = error.response?.data?.error || error.message;
        addLog(`Note creation failed: ${errorMsg}`, 'error');

        if (error.response?.data) {
          addLog(`Error details: ${JSON.stringify(error.response.data)}`, 'error');
        }

        if (errorMsg.includes('ObjectId') || errorMsg.includes('undefined')) {
          setSpecificError('Backend is receiving "undefined" as note ID. Check frontend note creation logic.');
        }

        testResults.push({
          name: 'Note Creation',
          status: false,
          message: `Failed: ${errorMsg}`
        });
      }
    } else {
      addLog('Skipping note tests - no token', 'warning');
      testResults.push({
        name: 'Note Creation',
        status: false,
        message: 'Skipped - No token'
      });
    }

    setTests(testResults);
    setErrorCount(errors);
    setLoading(false);
    addLog(`Tests completed. ${errors} error(s) found.`, errors > 0 ? 'error' : 'success');
  };

  const clearToken = () => {
    localStorage.removeItem('token');
    addLog('Token cleared. Reloading page...', 'info');
    setTimeout(() => window.location.reload(), 1000);
  };

  const resetAuth = () => {
    localStorage.clear();
    addLog('All localStorage cleared. Redirecting to login...', 'info');
    setTimeout(() => window.location.href = '/login', 1000);
  };

  const diagnoseObjectIdIssue = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token available');
      return;
    }

    addLog('=== Diagnosing ObjectId Issue ===', 'info');

    // Test 1: Try to get existing notes to see their structure
    try {
      addLog('Getting existing notes to analyze structure...', 'info');
      const notesResponse = await axios.get(`${backendUrl}/api/notes`, {
        headers: { 'Authorization': `Bearer ${token}` },
        params: { limit: 1 }
      });

      if (notesResponse.data && Array.isArray(notesResponse.data) && notesResponse.data.length > 0) {
        const sampleNote = notesResponse.data[0];
        addLog(`Sample note structure: ${JSON.stringify(sampleNote, null, 2)}`, 'info');

        const sampleId = sampleNote.id || sampleNote._id;
        if (sampleId) {
          addLog(`Sample note ID: ${sampleId}`, 'info');
          addLog(`Is valid ObjectId: ${/^[0-9a-fA-F]{24}$/.test(sampleId)}`, 'info');
        }
      }
    } catch (error) {
      addLog(`Failed to get notes: ${error.message}`, 'error');
    }

    // Test 2: Create a minimal note with explicit fields
    try {
      addLog('Creating minimal test note...', 'info');
      const minimalNote = { title: 'Minimal Test', content: 'Test' };
      const response = await axios.post(`${backendUrl}/api/notes`, minimalNote, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      console.group('=== NOTE CREATION RESPONSE ===');
      console.log('Full response:', response);
      console.log('Response data:', response.data);
      console.log('Response data keys:', Object.keys(response.data));
      console.log('Type of response.data:', typeof response.data);
      console.groupEnd();

      addLog('Response logged to console. Check browser console (F12) for details.', 'info');

      // Try to extract ID
      const id = response.data.id || response.data._id;
      if (id) {
        alert(`Note created! ID: ${id}\n\nCheck console for full response structure.`);
      } else {
        alert(`Note created but no ID found!\n\nCheck console for response structure.`);
      }
    } catch (error) {
      console.error('Note creation error:', error);
      alert(`Error: ${error.response?.data?.error || error.message}\n\nCheck console for details.`);
    }
  };

  const fixFrontendCode = () => {
    const fixCode = `
// FRONTEND FIX for "Invalid note ID format: 'undefined'"

// In your note creation function, replace:
