import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Notefditor.css'; // Assuming you have styles

const NoteEditor = ({ isAuthenticated, user, authLoading }) => {
  const { noteId } = useParams();
  const navigate = useNavigate();

  const [note, setNote] = useState(null);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [tags, setTags] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isNewNote, setIsNewNote] = useState(false);
  const [characterCount, setCharacterCount] = useState(0);
  const [wordCount, setWordCount] = useState(0);

  // Debug logging (remove in production)
  useEffect(() => {
    console.log('=== NOTE EDITOR DEBUG ===');
    console.log('Note ID from URL:', noteId);
    console.log('User ID:', user?._id);
    console.log('Is Authenticated:', isAuthenticated);
    console.log('Auth Loading:', authLoading);
  }, [noteId, user, isAuthenticated, authLoading]);

  // Validate note ID format
  const isValidObjectId = useCallback((id) => {
    return /^[0-9a-fA-F]{24}$/.test(id);
  }, []);

  // Check if this is a new note
  useEffect(() => {
    if (!noteId || noteId === 'undefined' || noteId === 'new') {
      setIsNewNote(true);
      setLoading(false);
      setTitle('');
      setContent('');
      setTags('');
      setError('');
    } else if (!isValidObjectId(noteId)) {
      setError('Invalid note ID format. Expected 24-character hex string.');
      setLoading(false);
    } else {
      setIsNewNote(false);
    }
  }, [noteId, isValidObjectId]);

  // Fetch note when authenticated and valid note ID
  const fetchNote = useCallback(async () => {
    if (!isAuthenticated || !user?._id) {
      console.log('Not authenticated or no user ID');
      return;
    }

    if (!noteId || noteId === 'undefined' || noteId === 'new' || !isValidObjectId(noteId)) {
      return;
    }

    try {
      setLoading(true);
      setError('');

      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found');
      }

      console.log('Fetching note with ID:', noteId);
      console.log('User ID for request:', user._id);

      const response = await axios.get(`/api/notes/${noteId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data) {
        setNote(response.data);
        setTitle(response.data.title || '');
        setContent(response.data.content || '');
        setTags(response.data.tags?.join(', ') || '');
        updateCounts(response.data.content || '');
      }
    } catch (err) {
      console.error('Failed to fetch note:', err);
      if (err.response?.status === 404) {
        setError('Note not found. It may have been deleted or you don\'t have permission.');
      } else if (err.response?.status === 400) {
        setError('Invalid note ID format.');
      } else {
        setError('Failed to load note. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  }, [noteId, isAuthenticated, user, isValidObjectId]);

  // Main effect to handle authentication and data fetching
  useEffect(() => {
    const initializeEditor = async () => {
      // Wait for auth to load
      if (authLoading) {
        console.log('Auth still loading...');
        return;
      }

      // Check authentication
      if (!isAuthenticated) {
        console.log('User not authenticated, redirecting...');
        navigate('/login');
        return;
      }

      // Check user ID
      if (!user?._id) {
        console.error('User ID is undefined');
        setError('User information not available. Please log in again.');
        return;
      }

      console.log('User authenticated with ID:', user._id);

      // Handle note fetching
      if (isNewNote) {
        setLoading(false);
        setError('');
      } else {
        await fetchNote();
      }
    };

    initializeEditor();
  }, [authLoading, isAuthenticated, user, navigate, isNewNote, fetchNote]);

  // Update character and word counts
  const updateCounts = (text) => {
    setCharacterCount(text.length);
    const words = text.trim().split(/\s+/).filter(word => word.length > 0);
    setWordCount(text.trim() === '' ? 0 : words.length);
  };

  // Handle content changes
  const handleContentChange = (e) => {
    const newContent = e.target.value;
    setContent(newContent);
    updateCounts(newContent);
  };

  // Save note
  const handleSave = async () => {
    if (!isAuthenticated || !user?._id) {
      setError('You must be logged in to save notes');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const noteData = {
        title,
        content,
        tags: tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        userId: user._id
      };

      let response;
      if (isNewNote) {
        response = await axios.post('/api/notes', noteData, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        navigate(`/notes/${response.data._id}`); // Redirect to the new note
      } else {
        response = await axios.put(`/api/notes/${noteId}`, noteData, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
      }

      // Show success message (you could add a toast notification here)
      console.log('Note saved successfully:', response.data);
    } catch (err) {
      console.error('Failed to save note:', err);
      setError('Failed to save note. Please try again.');
    }
  };

  // Redirect to notes list
  const handleViewAllNotes = () => {
    navigate('/notes');
  };

  // Create new note
  const handleCreateNewNote = () => {
    navigate('/notes/new');
  };

  if (loading) {
    return (
      <div className="note-editor loading">
        <div className="loading-spinner"></div>
        <p>Loading note...</p>
      </div>
    );
  }

  return (
    <div className="note-editor">
      {/* Dashboard Panel */}
      <div className="dashboard-panel">
        <h2>Umbra Dashboard</h2>

        <div className="status-section">
          <h3>Status</h3>
          <div className="status-item">
            <span className="status-label">API:</span>
            <span className={`status-value ${error ? 'error' : 'success'}`}>
              {error ? 'Error' : 'Connected'}
            </span>
          </div>
          <div className="status-item">
            <span className="status-label">Auth:</span>
            <span className={`status-value ${isAuthenticated ? 'success' : 'error'}`}>
              {isAuthenticated ? 'Logged In' : 'Logged Out'}
            </span>
          </div>
        </div>

        <div className="note-details">
          <h3>Note Details</h3>
          <div className="detail-item">
            <span>Note ID:</span>
            <span className={isValidObjectId(noteId) ? 'valid' : 'invalid'}>
              {noteId || 'N/A'}
            </span>
          </div>
          <div className="detail-item">
            <span>Is New Note:</span>
            <span className={isNewNote ? 'valid' : 'invalid'}>
              {isNewNote ? 'Yes' : 'No'}
            </span>
          </div>
          <div className="detail-item">
            <span>Valid ObjectId:</span>
            <span className={isValidObjectId(noteId) ? 'valid' : 'invalid'}>
              {isValidObjectId(noteId) ? 'Yes' : 'No'}
            </span>
          </div>
          <div className="detail-item">
            <span>User ID:</span>
            <span className={user?._id ? 'valid' : 'invalid'}>
              {user?._id || 'Undefined'}
            </span>
          </div>
        </div>

        {/* Error Message Display */}
        {error && (
          <div className="error-message">
            <p>{error}</p>
            <button onClick={handleViewAllNotes} className="btn-secondary">
              View All Notes
            </button>
          </div>
        )}

        {/* Action Buttons */}
        <div className="action-buttons">
          <button onClick={handleViewAllNotes} className="btn-primary">
            View All Notes
          </button>
          <button onClick={handleCreateNewNote} className="btn-secondary">
            Create New Note
          </button>
          <button onClick={handleSave} className="btn-success" disabled={!isAuthenticated}>
            Save Note
          </button>
        </div>

        {/* Stats */}
        <div className="stats">
          <div className="stat-item">
            <span>Characters:</span>
            <span>{characterCount}</span>
          </div>
          <div className="stat-item">
            <span>Words:</span>
            <span>{wordCount}</span>
          </div>
          <div className="stat-item">
            <span>Status:</span>
            <span className={isAuthenticated ? 'connected' : 'disconnected'}>
              {isAuthenticated ? 'Logged in' : 'Logged out'}
            </span>
          </div>
        </div>
      </div>

      {/* Note Editor Area */}
      <div className="editor-area">
        {error && !isNewNote ? (
          <div className="error-full">
            <h3>Unable to Load Note</h3>
            <p>{error}</p>
            <div className="error-actions">
              <button onClick={handleViewAllNotes} className="btn-primary">
                Go to Notes List
              </button>
              <button onClick={handleCreateNewNote} className="btn-secondary">
                Create New Note
              </button>
            </div>
          </div>
        ) : (
          <>
            <div className="editor-header">
              <input
                type="text"
                className="note-title"
                placeholder="Note Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
              <input
                type="text"
                className="note-tags"
                placeholder="Tags (comma separated)"
                value={tags}
                onChange={(e) => setTags(e.target.value)}
              />
            </div>

            <textarea
              className="note-content"
              placeholder="Start writing your note here..."
              value={content}
              onChange={handleContentChange}
              rows="20"
            />

            <div className="editor-footer">
              <div className="auto-save-status">
                <span className="status-indicator connected"></span>
                Auto-save {isAuthenticated ? 'enabled' : 'disabled'}
              </div>
              <div className="encryption-status">
                <span className="status-indicator"></span>
                Not Encrypted
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default NoteEditor;
