import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { FaLock, FaEnvelope, FaUser, FaKey } from 'react-icons/fa';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [masterPassword, setMasterPassword] = useState('');
  const [showMasterPassword, setShowMasterPassword] = useState(false);

  const navigate = useNavigate();
  const { register } = useAuth();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Simple password generator
  const generatePassword = (length = 16) => {
    const charset = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*';
    let password = '';

    // Use window.crypto if available, otherwise fallback
    if (typeof window !== 'undefined' && window.crypto) {
      const randomValues = new Uint32Array(length);
      window.crypto.getRandomValues(randomValues);

      for (let i = 0; i < length; i++) {
        password += charset[randomValues[i] % charset.length];
      }
    } else {
      // Fallback
      for (let i = 0; i < length; i++) {
        const randomIndex = Math.floor(Math.random() * charset.length);
        password += charset[randomIndex];
      }
    }

    return password;
  };

  const handleGeneratePassword = (e) => {
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }

    const password = generatePassword(16);
    setMasterPassword(password);
    setShowMasterPassword(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Validation
    if (!formData.username.trim()) {
      setError('Username is required');
      return;
    }

    if (!formData.email.includes('@')) {
      setError('Please enter a valid email address');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    if (!masterPassword) {
      setError('Please generate a master password');
      return;
    }

    setLoading(true);

    try {
      // Simplified crypto for testing
      // Generate simple key pair
      const timestamp = Date.now();
      const randomStr = Math.random().toString(36).substring(2, 15);

      const keyPair = {
        publicKey: `pub_${randomStr}_${timestamp}`,
        privateKey: `priv_${randomStr}_${timestamp}`
      };

      // Simple "encryption" for private key (base64)
      const encryptedPrivateKey = JSON.stringify({
        ciphertext: btoa(keyPair.privateKey), // Convert to base64
        salt: btoa(`salt_${timestamp}`)
      });

      // Prepare registration data
      const userData = {
        username: formData.username.trim(),
        email: formData.email.trim(),
        password: formData.password,
        publicKey: keyPair.publicKey,
        encryptedPrivateKey: encryptedPrivateKey
      };

      console.log('Attempting registration with:', {
        username: userData.username,
        email: userData.email
      });

      const result = await register(userData);

      if (result.success) {
        // Show master password to user
        alert(`IMPORTANT: Save this master password securely:\n\n${masterPassword}\n\nYou will need it to decrypt your notes.`);
        navigate('/');
      } else {
        setError(result.error);
      }
    } catch (error) {
      console.error('Registration error:', error);
      setError(`Registration failed: ${error.message || 'Unknown error'}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Create your account
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Join Umbra for secure, encrypted note-taking
          </p>
        </div>

        <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
          {error && (
            <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded">
              {error}
            </div>
          )}

          <div className="rounded-md shadow-sm space-y-4">
            <div className="relative">
              <label htmlFor="username" className="sr-only">Username</label>
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaUser className="h-5 w-5 text-gray-400" />
              </div>
              <input
                id="username"
                name="username"
                type="text"
                required
                value={formData.username}
                onChange={handleChange}
                className="appearance-none relative block w-full px-12 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm"
                placeholder="Username (min 3 characters)"
              />
            </div>

            <div className="relative">
              <label htmlFor="email" className="sr-only">Email address</label>
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaEnvelope className="h-5 w-5 text-gray-400" />
              </div>
              <input
                id="email"
                name="email"
                type="email"
                required
                value={formData.email}
                onChange={handleChange}
                className="appearance-none relative block w-full px-12 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm"
                placeholder="Email address (e.g., user@example.com)"
              />
            </div>

            <div className="relative">
              <label htmlFor="password" className="sr-only">Password</label>
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaLock className="h-5 w-5 text-gray-400" />
              </div>
              <input
                id="password"
                name="password"
                type="password"
                required
                value={formData.password}
                onChange={handleChange}
                className="appearance-none relative block w-full px-12 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm"
                placeholder="Password (min 6 characters)"
              />
            </div>

            <div className="relative">
              <label htmlFor="confirmPassword" className="sr-only">Confirm Password</label>
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaLock className="h-5 w-5 text-gray-400" />
              </div>
              <input
                id="confirmPassword"
                name="confirmPassword"
                type="password"
                required
                value={formData.confirmPassword}
                onChange={handleChange}
                className="appearance-none relative block w-full px-12 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm"
                placeholder="Confirm Password"
              />
            </div>
          </div>

          <div className="p-4 bg-yellow-50 border border-yellow-200 rounded-md">
            <div className="flex items-center justify-between mb-2">
              <div className="font-medium text-yellow-800 flex items-center">
                <FaKey className="mr-2" />
                Master Password
              </div>
              <button
                type="button"
                onClick={handleGeneratePassword}
                className="px-3 py-1 text-sm bg-blue-100 text-blue-600 rounded hover:bg-blue-200"
              >
                Generate
              </button>
            </div>
            <p className="text-sm text-yellow-600 mb-3">
              This password encrypts your private key. Save it securely - it cannot be recovered!
            </p>
            {showMasterPassword && (
              <div className="p-3 bg-white border rounded">
                <div className="font-mono text-sm break-all bg-gray-100 p-2 rounded">
                  {masterPassword}
                </div>
                <div className="text-xs text-red-600 mt-2 font-medium">
                  ⚠️ IMPORTANT: Copy and save this password now! You will need it to access your notes.
                </div>
              </div>
            )}
          </div>

          <div>
            <button
              type="submit"
              disabled={loading || !showMasterPassword}
              className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
            >
              {loading ? 'Creating account...' : 'Register'}
            </button>
          </div>

          <div className="text-center">
            <Link
              to="/login"
              className="font-medium text-blue-600 hover:text-blue-500"
            >
              Already have an account? Sign in
            </Link>
          </div>
        </form>

        <div className="mt-6 p-4 bg-blue-50 rounded-md text-sm text-gray-600">
          <p className="font-medium mb-2">Test Credentials:</p>
          <ul className="list-disc pl-5 space-y-1">
            <li>Username: testuser (min 3 chars)</li>
            <li>Email: test@example.com (must have @ and .)</li>
            <li>Password: any 6+ characters</li>
            <li>Click "Generate" for Master Password</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Register;
