const express = require('express');
const mongoose = require('mongoose');
const cors = require('cors');
require('dotenv').config();

const app = express();

// Middleware
app.use(cors());
app.use(express.json());

// Database connection
mongoose.connect(process.env.MONGODB_URI || 'mongodb://localhost:27017/encrypted-notes')
  .then(() => console.log('âœ… MongoDB connected'))
  .catch(err => console.error('âŒ MongoDB connection error:', err));

// Test route
app.get('/', (req, res) => {
  res.json({ message: 'Encrypted Notes API is running' });
});

// Import routes
const authRoutes = require('./routes/auth');
const noteRoutes = require('./routes/notes');
const shareRoutes = require('./routes/share');
const userRoutes = require('./routes/users');
const testRoutes = require('./routes/test'); // Add test routes

// Mount routes
app.use('/api', testRoutes); // Mount test routes at /api prefix
app.use('/api/auth', authRoutes);
app.use('/api/notes', noteRoutes);
app.use('/api/share', shareRoutes);
app.use('/api/users', userRoutes);

// Error handling middleware
app.use((err, req, res, next) => {
  console.error('âŒ Server error:', err.stack);
  res.status(500).json({ error: 'Something went wrong!' });
});

// Start server
const PORT = process.env.PORT || 5000;
app.listen(PORT, () => {
  console.log(`ðŸš€ Server running on port ${PORT}`);
  console.log(`ðŸ“¡ Available routes:`);
  console.log(`   GET  /api/test`);
  console.log(`   GET  /api/health`);
  console.log(`   GET  /api/users/search?q=query`);
  console.log(`   GET  /api/notes`);
  console.log(`   POST /api/share/:noteId`);
  console.log(`   GET  /api/share/shared-with-me`);
});
