import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useCrypto } from '../contexts/CryptoContext';
import {
  FaLock, FaEnvelope, FaUser, FaKey, FaCheck, FaCopy,
  FaShieldAlt, FaEye, FaEyeSlash, FaDownload, FaQrcode
} from 'react-icons/fa';
import { QRCodeSVG } from 'qrcode.react';
import { exportEncryptedData } from '../utils/cryptoUtils';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [passwordStrength, setPasswordStrength] = useState(0);
  const [copied, setCopied] = useState({ public: false, private: false });
  const [generatedKeys, setGeneratedKeys] = useState(null);
  const [showPublicKey, setShowPublicKey] = useState(false);
  const [showPrivateKey, setShowPrivateKey] = useState(false);
  const [showQR, setShowQR] = useState({ public: false, private: false });

  const navigate = useNavigate();
  const { register } = useAuth();
  const { generateRSAKeyPair, deriveKeyFromPassword, setMasterKey, setPrivateKey } = useCrypto();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Calculate password strength
    if (name === 'password') {
      let strength = 0;
      if (value.length >= 8) strength += 25;
      if (/[A-Z]/.test(value)) strength += 25;
      if (/[0-9]/.test(value)) strength += 25;
      if (/[^A-Za-z0-9]/.test(value)) strength += 25;
      setPasswordStrength(strength);
    }
  };

  const generateKeys = async () => {
    if (formData.password.length < 6) {
      setError('Please set a strong account password first');
      return;
    }

    // Generate real RSA key pair
    try {
      const keyPair = await generateRSAKeyPair();

      setGeneratedKeys({
        publicKey: keyPair.publicKey,
        privateKey: keyPair.privateKey,
        timestamp: Date.now()
      });

      setCopied({ public: false, private: false });
    } catch (error) {
      console.error('Failed to generate keys:', error);
      setError('Failed to generate encryption keys. Please try again.');
    }
  };

  const copyToClipboard = (text, type) => {
    navigator.clipboard.writeText(text);
    setCopied({ ...copied, [type]: true });
    setTimeout(() => setCopied({ ...copied, [type]: false }), 2000);
  };

  const downloadKey = (key, filename) => {
    const element = document.createElement('a');
    const file = new Blob([key], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = filename;
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  const getStrengthColor = (strength) => {
    if (strength < 50) return 'bg-red-500';
    if (strength < 75) return 'bg-yellow-500';
    return 'bg-green-500';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Validation
    if (!formData.username.trim()) {
      setError('Username is required');
      return;
    }

    if (!formData.email.includes('@')) {
      setError('Please enter a valid email address');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    if (!generatedKeys) {
      setError('Please generate encryption keys first');
      return;
    }

    setLoading(true);

    try {
      // Prepare registration data
      const userData = {
        username: formData.username.trim(),
        email: formData.email.trim(),
        password: formData.password,
        publicKey: generatedKeys.publicKey,
        encryptedPrivateKey: JSON.stringify(exportEncryptedData(generatedKeys.privateKey, formData.password))
      };

      const result = await register(userData);

      if (result.success) {
        // Auto-set master key so user is ready to go
        try {
          const derivedKey = deriveKeyFromPassword(formData.password, 'master-salt');
          setMasterKey(derivedKey);
          setPrivateKey(generatedKeys.privateKey);
          console.log('‚úÖ Master key and private key auto-set after registration');
        } catch (err) {
          console.error('Failed to auto-set master key:', err);
        }

        // Show success modal with all keys
        const modal = document.createElement('div');
