import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaCheck, FaTimes, FaKey, FaUser, FaRedo, FaExclamationTriangle, FaBug, FaDatabase, FaServer } from 'react-icons/fa';

const DebugAuth = () => {
  const [tests, setTests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errorCount, setErrorCount] = useState(0);
  const [backendUrl, setBackendUrl] = useState('http://localhost:5000');
  const [debugLog, setDebugLog] = useState([]);
  const [apiEndpoints, setApiEndpoints] = useState([]);

  useEffect(() => {
    runTests();
    discoverEndpoints();
  }, []);

  const addLog = (message, type = 'info') => {
    const timestamp = new Date().toLocaleTimeString();
    setDebugLog(prev => [...prev.slice(-50), { message: `[${timestamp}] ${message}`, type }]);
  };

  const discoverEndpoints = async () => {
    try {
      const response = await axios.get(`${backendUrl}/api`);
      if (response.data && typeof response.data === 'object') {
        const endpoints = Object.keys(response.data).map(key => ({
          name: key,
          path: `/api/${key}`,
          description: response.data[key]
        }));
        setApiEndpoints(endpoints);
        addLog(`Discovered ${endpoints.length} API endpoints`);
      }
    } catch (error) {
      addLog(`Failed to discover API endpoints: ${error.message}`, 'warning');
    }
  };

  const runTests = async () => {
    setLoading(true);
    setDebugLog([]);
    const testResults = [];
    let errors = 0;

    addLog('=== Starting Authentication Debug Tests ===', 'info');

    // Test 1: Check localStorage token
    const token = localStorage.getItem('token');
    const hasToken = !!token;

    addLog(`Token Check: ${hasToken ? 'Token found in localStorage' : 'No token found in localStorage'}`);

    testResults.push({
      name: 'Token in localStorage',
      status: hasToken,
      message: token ? `Token found (${token.length} characters)` : 'No token found'
    });

    if (hasToken) {
      addLog(`Token Preview: ${token.substring(0, 50)}...`, 'info');

      // Try to decode JWT token (if it's a JWT)
      try {
        const tokenParts = token.split('.');
        if (tokenParts.length === 3) {
          const payload = JSON.parse(atob(tokenParts[1]));
          addLog(`Token Payload: ${JSON.stringify(payload)}`, 'info');
        }
      } catch (e) {
        addLog('Token is not a standard JWT format', 'warning');
      }
    }

    // Test 2: Check backend health
    try {
      addLog(`Testing Backend Health: ${backendUrl}/health`);
      const healthResponse = await axios.get(`${backendUrl}/health`);
      const isHealthy = healthResponse.status === 200;

      addLog(`Backend Health Response: ${JSON.stringify(healthResponse.data)}`);

      testResults.push({
        name: 'Backend Health',
        status: isHealthy,
        message: healthResponse.data?.status || 'Backend is responding'
      });
    } catch (error) {
      errors++;
      addLog(`❌ Backend Health Check Failed: ${error.message}`, 'error');
      addLog(`Request URL: ${error.config?.url}`, 'error');
      addLog(`Response Status: ${error.response?.status}`, 'error');

      testResults.push({
        name: 'Backend Health',
        status: false,
        message: `Connection failed: ${error.message}`
      });
    }

    // Test 3: Validate token with backend
    if (token) {
      try {
        addLog(`Validating Token: ${backendUrl}/api/auth/me`);
        const authResponse = await axios.get(`${backendUrl}/api/auth/me`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        addLog(`Auth Response: ${JSON.stringify(authResponse.data)}`);

        const userData = authResponse.data.user || authResponse.data;
        const userEmail = userData.email || userData.username || 'Unknown User';
        const userId = userData.id || userData._id || 'No ID';

        addLog(`✅ Token Validated: ${userEmail} (ID: ${userId})`);

        testResults.push({
          name: 'Token Validation',
          status: true,
          message: `Valid - User: ${userEmail}`
        });
      } catch (error) {
        errors++;
        const status = error.response?.status;
        const errorData = error.response?.data;

        addLog(`❌ Token Validation Failed: Status ${status}`, 'error');
        addLog(`Error Details: ${JSON.stringify(errorData)}`, 'error');

        testResults.push({
          name: 'Token Validation',
          status: false,
          message: `HTTP ${status}: ${errorData?.error || error.message}`
        });
      }
    } else {
      addLog('⚠️ Skipping token validation - no token available', 'warning');
      testResults.push({
        name: 'Token Validation',
        status: false,
        message: 'No token to validate'
      });
    }

    // Test 4: Test note creation - with multiple attempts and different formats
    if (token) {
      let noteCreationSuccess = false;
      let noteId = null;
      let responseStructure = null;

      // Try different note formats
      const testNoteFormats = [
        {
          title: 'Debug Test Note 1',
          content: 'Test content from debugger',
          tags: ['debug', 'test']
        },
        {
          title: 'Debug Test Note',
          content: 'Simple test note',
          category: 'test',
          isPublic: true
        },
        {
          note: {
            title: 'Nested Test Note',
            content: 'Nested structure test',
            tags: ['nested']
          }
        }
      ];

      for (let i = 0; i < testNoteFormats.length; i++) {
        if (noteCreationSuccess) break;

        const testNote = testNoteFormats[i];
        addLog(`Attempt ${i + 1}: Testing note creation with format ${JSON.stringify(testNote).substring(0, 50)}...`);

        try {
          const noteResponse = await axios.post(`${backendUrl}/api/notes`, testNote, {
            headers: { 'Authorization': `Bearer ${token}` }
          });

          responseStructure = noteResponse.data;
          addLog(`Note Creation Response Structure:`, 'info');
          addLog(JSON.stringify(responseStructure, null, 2), 'info');

          // Try to extract ID from various possible locations
          const possibleIdPaths = [
            'id',
            '_id',
            'note.id',
            'note._id',
            'data.id',
            'data._id',
            'result.id',
            'result._id',
            'createdNote.id',
            'createdNote._id'
          ];

          for (const path of possibleIdPaths) {
            const value = getNestedValue(responseStructure, path);
            if (value && typeof value === 'string' && value.length > 0) {
              noteId = value;
              addLog(`✅ Found ID at path "${path}": ${noteId}`, 'success');
              break;
            }
          }

          if (noteId) {
            noteCreationSuccess = true;
            addLog(`✅ Note created successfully with ID: ${noteId}`);

            // Validate if it looks like an ObjectId
            const isObjectId = /^[0-9a-fA-F]{24}$/.test(noteId);
            addLog(`ID Format: ${isObjectId ? 'Looks like MongoDB ObjectId (24 hex chars)' : 'Non-standard ID format'}`);

            testResults.push({
              name: 'Note Creation',
              status: true,
              message: `Created - ID: ${noteId.substring(0, 20)}...`
            });

            // Clean up - try to delete the test note
            try {
              addLog(`Attempting to delete test note: ${noteId}`);
              await axios.delete(`${backendUrl}/api/notes/${noteId}`, {
                headers: { 'Authorization': `Bearer ${token}` }
              });
              addLog('✅ Test note deleted successfully');

              testResults.push({
                name: 'Note Cleanup',
                status: true,
                message: 'Test note cleaned up'
              });
            } catch (deleteError) {
              addLog(`⚠️ Failed to delete test note: ${deleteError.message}`, 'warning');
              testResults.push({
                name: 'Note Cleanup',
                status: false,
                message: `Delete failed: ${deleteError.message}`
              });
            }

            break; // Success, exit loop
          } else {
            addLog(`⚠️ Note created but no ID found in response. Full keys: ${Object.keys(responseStructure).join(', ')}`, 'warning');

            // Try alternative API endpoints
            const alternativeEndpoints = ['/api/note', '/api/notes/create', '/api/note/create'];
            for (const endpoint of alternativeEndpoints) {
              addLog(`Trying alternative endpoint: ${endpoint}`);
              try {
                const altResponse = await axios.post(`${backendUrl}${endpoint}`, testNote, {
                  headers: { 'Authorization': `Bearer ${token}` }
                });
                addLog(`Alternative endpoint response: ${JSON.stringify(altResponse.data)}`);
              } catch (altError) {
                // Ignore alternative endpoint errors
              }
            }
          }
        } catch (error) {
          addLog(`❌ Note creation attempt ${i + 1} failed: ${error.message}`, 'error');
          if (error.response) {
            addLog(`Response status: ${error.response.status}`, 'error');
            addLog(`Response data: ${JSON.stringify(error.response.data)}`, 'error');
          }
        }
      }

      if (!noteCreationSuccess) {
        errors++;
        addLog('❌ All note creation attempts failed', 'error');
        testResults.push({
          name: 'Note Creation',
          status: false,
          message: 'Failed to create test note'
        });

        // Try to GET existing notes to understand the structure
        try {
          addLog('Attempting to GET existing notes to understand structure...');
          const notesResponse = await axios.get(`${backendUrl}/api/notes`, {
            headers: { 'Authorization': `Bearer ${token}` }
          });
          addLog(`Existing notes structure: ${JSON.stringify(notesResponse.data, null, 2)}`);

          if (Array.isArray(notesResponse.data) && notesResponse.data.length > 0) {
            const sampleNote = notesResponse.data[0];
            const sampleId = sampleNote.id || sampleNote._id;
            addLog(`Sample note ID from GET: ${sampleId}`);
          }
        } catch (getError) {
          addLog(`Failed to GET notes: ${getError.message}`, 'warning');
        }
      }
    } else {
      addLog('⚠️ Skipping note creation tests - no token', 'warning');
      testResults.push({
        name: 'Note Creation',
        status: false,
        message: 'Skipped - No authentication token'
      });
    }

    // Test 5: Test other API endpoints
    addLog('Testing other API endpoints...');
    const endpointsToTest = ['/api/users/me', '/api/profile', '/api/settings'];

    for (const endpoint of endpointsToTest) {
      if (token) {
        try {
          const response = await axios.get(`${backendUrl}${endpoint}`, {
            headers: { 'Authorization': `Bearer ${token}` }
          });
          addLog(`✅ ${endpoint}: ${response.status}`);
        } catch (error) {
          // Expected for endpoints that don't exist
        }
      }
    }

    setTests(testResults);
    setErrorCount(errors);
    setLoading(false);
    addLog(`=== Tests Complete: ${errors} error(s), ${tests.filter(t => t.status).length} passed ===`, errors > 0 ? 'error' : 'success');
  };

  // Helper function to get nested value
  const getNestedValue = (obj, path) => {
    return path.split('.').reduce((current, key) => {
      return current && current[key] !== undefined ? current[key] : undefined;
    }, obj);
  };

  const clearToken = () => {
    localStorage.removeItem('token');
    addLog('Token cleared from localStorage');
    window.location.reload();
  };

  const resetAuth = () => {
    localStorage.clear();
    sessionStorage.clear();
    addLog('All storage cleared, redirecting to login');
    window.location.href = '/login';
  };

  const copyToken = () => {
    const token = localStorage.getItem('token');
    if (token) {
      navigator.clipboard.writeText(token);
      addLog('Token copied to clipboard');
      alert('Token copied to clipboard!');
    }
  };

  const deepInspectAPI = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token available');
      return;
    }

    addLog('=== Starting Deep API Inspection ===', 'info');

    // Test all common endpoints
    const endpoints = [
      { method: 'GET', path: '/api/notes', description: 'Get all notes' },
      { method: 'GET', path: '/api/notes?limit=1', description: 'Get one note' },
      { method: 'POST', path: '/api/notes', description: 'Create note' },
      { method: 'GET', path: '/api', description: 'API root' },
      { method: 'GET', path: '/api/auth/me', description: 'Current user' },
      { method: 'GET', path: '/api/users/me', description: 'User profile' }
    ];

    for (const endpoint of endpoints) {
      try {
        addLog(`Testing ${endpoint.method} ${endpoint.path}: ${endpoint.description}`);

        const config = {
          headers: { 'Authorization': `Bearer ${token}` }
        };

        let response;
        if (endpoint.method === 'POST' && endpoint.path === '/api/notes') {
          response = await axios.post(
            `${backendUrl}${endpoint.path}`,
            { title: 'Deep Inspection Note', content: 'Testing...' },
            config
          );
        } else {
          response = await axios.get(`${backendUrl}${endpoint.path}`, config);
        }

        console.group(`${endpoint.method} ${endpoint.path}`);
        console.log('Status:', response.status);
        console.log('Headers:', response.headers);
        console.log('Data:', response.data);
        console.log('Full Response:', response);
        console.groupEnd();

        addLog(`✅ ${endpoint.method} ${endpoint.path}: Success`);
        addLog(`   Data keys: ${Object.keys(response.data).join(', ')}`);

        if (response.data && typeof response.data === 'object') {
          // Look for ID fields
          const findIds = (obj, path = '') => {
            for (const key in obj) {
              const currentPath = path ? `${path}.${key}` : key;
              const value = obj[key];

              if (key.toLowerCase().includes('id') && typeof value === 'string') {
                addLog(`   Found ID at ${currentPath}: ${value}`);
                console.log(`ID at ${currentPath}:`, value);
              }

              if (typeof value === 'object' && value !== null) {
                findIds(value, currentPath);
              }
            }
          };

          findIds(response.data);
        }
      } catch (error) {
        addLog(`❌ ${endpoint.method} ${endpoint.path}: ${error.message}`, 'error');
        if (error.response) {
          console.error(`Error for ${endpoint.path}:`, error.response.data);
        }
      }
    }

    addLog('=== Deep Inspection Complete ===', 'info');
    alert('Deep inspection complete! Check console and debug log for details.');
  };

  const testObjectIdValidation = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token available');
      return;
    }

    // Test if backend accepts different ID formats
    const testIds = [
      '507f1f77bcf86cd799439011', // Valid ObjectId
      'test-note-id-123', // String ID
      '12345', // Numeric string
      '000000000000000000000000' // Zero ObjectId
    ];

    addLog('Testing ObjectId validation with different formats...', 'info');

    testIds.forEach(async (testId, index) => {
      try {
        const response = await axios.get(`${backendUrl}/api/notes/${testId}`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        addLog(`✅ Test ID "${testId}" (format ${index + 1}): Accepted - ${response.status}`);
      } catch (error) {
        addLog(`❌ Test ID "${testId}" (format ${index + 1}): Rejected - ${error.response?.status || error.message}`, 'error');
      }
    });
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-4 md:p-8">
      <div className="max-w-7xl mx-auto">
        <header className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2 flex items-center gap-3">
                <FaBug className="text-blue-600 dark:text-blue-400" />
                Authentication & API Debugger
              </h1>
              <p className="text-gray-600 dark:text-gray-400">
                Comprehensive debugging tool for authentication and API issues
              </p>
            </div>
            <div className="flex items-center gap-2">
              <FaServer className="text-gray-400" />
              <input
                type="text"
                value={backendUrl}
                onChange={(e) => setBackendUrl(e.target.value)}
                className="px-3 py-1 text-sm border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-white w-64"
                placeholder="http://localhost:5000"
              />
            </div>
          </div>
        </header>

        {/* Stats Bar */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <div className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500 dark:text-gray-400">Tests</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {tests.filter(t => t.status).length}/{tests.length}
                </p>
              </div>
              <div className={`w-10 h-10 rounded-full flex items-center justify-center ${errorCount === 0 ? 'bg-green-100 text-green-600 dark:bg-green-900 dark:text-green-300' : 'bg-red-100 text-red-600 dark:bg-red-900 dark:text-red-300'}`}>
                {errorCount === 0 ? <FaCheck /> : <FaTimes />}
              </div>
            </div>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500 dark:text-gray-400">Errors</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">{errorCount}</p>
              </div>
              <div className="w-10 h-10 rounded-full bg-red-100 text-red-600 dark:bg-red-900 dark:text-red-300 flex items-center justify-center">
                <FaExclamationTriangle />
              </div>
            </div>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500 dark:text-gray-400">Token</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {localStorage.getItem('token') ? '✓' : '✗'}
                </p>
              </div>
              <div className="w-10 h-10 rounded-full bg-blue-100 text-blue-600 dark:bg-blue-900 dark:text-blue-300 flex items-center justify-center">
                <FaKey />
              </div>
            </div>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500 dark:text-gray-400">Backend</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {backendUrl.includes('localhost') ? 'Local' : 'Remote'}
                </p>
              </div>
              <div className="w-10 h-10 rounded-full bg-purple-100 text-purple-600 dark:bg-purple-900 dark:text-purple-300 flex items-center justify-center">
                <FaDatabase />
              </div>
            </div>
          </div>
        </div>

        <div className="grid lg:grid-cols-3 gap-6 mb-8">
          {/* Main Test Panel */}
          <div className="lg:col-span-2">
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 mb-6">
              <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-6">
                <div>
                  <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">Diagnostic Tests</h2>
                  <p className="text-sm text-gray-500 dark:text-gray-400">
                    Run comprehensive tests to identify authentication and API issues
                  </p>
                </div>

                <div className="flex flex-wrap gap-2">
                  <button
                    onClick={runTests}
                    disabled={loading}
                    className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
                  >
                    <FaRedo className={loading ? 'animate-spin' : ''} />
                    {loading ? 'Running...' : 'Run All Tests'}
                  </button>
                </div>
              </div>

              {loading ? (
                <div className="text-center py-12">
                  <div className="w-16 h-16 border-4 border-blue-200 border-t-blue-600 rounded-full animate-spin mx-auto"></div>
                  <p className="mt-6 text-gray-500 dark:text-gray-400">Running comprehensive diagnostics...</p>
                  <p className="text-sm text-gray-400 dark:text-gray-500 mt-2">
                    Testing authentication, API endpoints, and data operations
                  </p>
                </div>
              ) : (
                <div className="space-y-4">
                  {tests.map((test, index) => (
                    <div
                      key={index}
                      className={`p-4 rounded-lg border transition-all duration-200 ${test.status ? 'bg-green-50 border-green-200 dark:bg-green-900/20 dark:border-green-800' : 'bg-red-50 border-red-200 dark:bg-red-900/20 dark:border-red-800'}`}
                    >
                      <div className="flex flex-col md:flex-row md:items-center justify-between gap-3">
                        <div className="flex items-center gap-3">
                          {test.status ? (
                            <div className="w-6 h-6 rounded-full bg-green-100 dark:bg-green-900 flex items-center justify-center">
                              <FaCheck className="text-green-600 dark:text-green-400 text-sm" />
                            </div>
                          ) : (
                            <div className="w-6 h-6 rounded-full bg-red-100 dark:bg-red-900 flex items-center justify-center">
                              <FaTimes className="text-red-600 dark:text-red-400 text-sm" />
                            </div>
                          )}
                          <div>
                            <span className="font-medium text-gray-900 dark:text-white">{test.name}</span>
                            {test.name.includes('Note') && !test.status && (
                              <span className="ml-3 text-xs px-2 py-1 bg-yellow-100 dark:bg-yellow-900 text-yellow-800 dark:text-yellow-300 rounded">
                                API Response Issue
                              </span>
                            )}
                          </div>
                        </div>
                        <span className={`text-sm ${test.status ? 'text-green-700 dark:text-green-300' : 'text-red-700 dark:text-red-300'} font-medium`}>
                          {test.message}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Debug Log */}
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">Debug Log</h3>
                <div className="flex gap-2">
                  <button
                    onClick={() => setDebugLog([])}
                    className="px-3 py-1 text-sm bg-gray-600 text-white rounded hover:bg-gray-700"
                  >
                    Clear
                  </button>
                  <button
                    onClick={() => console.log('Debug Log:', debugLog)}
                    className="px-3 py-1 text-sm bg-gray-600 text-white rounded hover:bg-gray-700"
                  >
                    To Console
                  </button>
                </div>
              </div>
              <div className="h-80 overflow-y-auto bg-gray-900 text-gray-100 rounded-lg p-4 font-mono text-sm">
                {debugLog.length === 0 ? (
                  <div className="text-gray-400 italic">Run tests to see debug information...</div>
                ) : (
                  debugLog.map((log, index) => (
                    <div 
                      key={index} 
                      className={`mb-1 ${log.type === 'error' ? 'text-red-400' : log.type === 'warning' ? 'text-yellow-400' : log.type === 'success' ? 'text-green-400' : 'text-blue-400'}`}
                    >
                      {log.message}
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>

          {/* Actions Panel */}
          <div className="space-y-6">
            {/* Token Management */}
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 flex items-center gap-2">
                <FaKey className="text-gray-400" />
                Token Actions
              </h3>
              <div className="space-y-3">
                <div className="p-3 bg-gray-50 dark:bg-gray-900 rounded-lg">
                  <p className="text-sm text-gray-500 dark:text-gray-400">Current Token Status</p>
                  <p className={`font-medium ${localStorage.getItem('token') ? 'text-green-600 dark:text-green-400' : 'text-red-600 dark:text-red-400'}`}>
                    {localStorage.getItem('token') ? '✓ Present' : '✗ Missing'}
                  </p>
                </div>

                <button
                  onClick={copyToken}
                  disabled={!localStorage.getItem('token')}
                  className="w-full py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Copy Token to Clipboard
                </button>

                <button
                  onClick={clearToken}
                  className="w-full py-3 bg-red-600 text-white rounded-lg hover:bg-red-700"
                >
                  Clear Token Only
                </button>

                <button
                  onClick={() => window.location.href = '/login'}
                  className="w-full py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 flex items-center justify-center gap-2"
                >
                  <FaUser />
                  Go to Login
                </button>
              </div>
            </div>

            {/* Advanced Debugging */}
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Advanced Debugging</h3>
              <div className="space-y-3">
                <button
                  onClick={deepInspectAPI}
                  className="w-full py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700"
                >
                  Deep API Inspection
                </button>

                <button
                  onClick={testObjectIdValidation}
                  className="w-full py-3 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700"
                >
                  Test ID Formats
                </button>

                <button
                  onClick={() => {
                    localStorage.setItem('token', 'test_invalid_jwt_token.eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.test');
                    window.location.reload();
                  }}
                  className="w-full py-3 bg-orange-600 text-white rounded-lg hover:bg-orange-700"
                >
                  Set Test JWT Token
                </button>

                <button
                  onClick={resetAuth}
                  className="w-full py-3 bg-red-700 text-white rounded-lg hover:bg-red-800"
                >
                  Nuclear Reset (Logout)
                </button>
              </div>
            </div>

            {/* API Endpoints */}
            {apiEndpoints.length > 0 && (
              <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Discovered Endpoints</h3>
                <div className="space-y-2">
                  {apiEndpoints.slice(0, 5).map((endpoint, index) => (
                    <div key={index} className="p-2 bg-gray-50 dark:bg-gray-900 rounded">
                      <p className="text-sm font-medium text-gray-900 dark:text-white">{endpoint.name}</p>
                      <p className="text-xs text-gray-500 dark:text-gray-400">{endpoint.path}</p>
                    </div>
                  ))}
                  {apiEndpoints.length > 5 && (
                    <p className="text-sm text-gray-500 dark:text-gray-400 text-center">
                      +{apiEndpoints.length - 5} more endpoints
                    </p>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Troubleshooting Guide */}
        <div className="bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-xl p-6">
          <h3 className="text-lg font-semibold text-yellow-800 dark:text-yellow-300 mb-4 flex items-center gap-2">
            <FaExclamationTriangle />
            ObjectId Validation Troubleshooting
          </h3>

          <div className="grid md:grid-cols-2 gap-6 mb-4">
            <div>
              <h4 className="font-medium text-yellow-700 dark:text-yellow-400 mb-2">If "not a valid ObjectId":</h4>
              <ul className="space-y-2 text-yellow-700 dark:text-yellow-400">
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Click <strong>"Deep API Inspection"</strong> to examine all endpoints</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Use <strong>"Test ID Formats"</strong> to see what IDs your backend accepts</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Check if your backend uses string IDs instead of MongoDB ObjectIds</span>
                </li>
              </ul>
            </div>

            <div>
              <h4 className="font-medium text-yellow-700 dark:text-yellow-400 mb-2">Backend Solutions:</h4>
              <ul className="space-y-2 text-yellow-700 dark:text-yellow-400">
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Modify backend to accept both ObjectId and string IDs</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Check MongoDB schema for proper ID field naming</span>
                </li>
                <li className="flex items-start gap-2">
                  <span className="text-yellow-500">•</span>
                  <span>Verify response includes <code className="bg-yellow-100 dark:bg-yellow-900 px-1 rounded">id</code> field (not just <code className="bg-yellow-100 dark:bg-yellow-900 px-1 rounded">_id</code>)</span>
                </li>
              </ul>
            </div>
          </div>

          <div className="mt-4 p-4 bg-yellow-100 dark:bg-yellow-900/30 rounded-lg">
            <p className="text-sm text-yellow-800 dark:text-yellow-300">
              <strong>Quick Fix:</strong> If backend returns <code>_id</code> instead of <code>id</code>, modify your frontend 
              to use <code>response.data._id</code> or ask backend team to include both fields.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DebugAuth;
