import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { 
  FaSave, FaTimes, FaHistory, FaLock, FaKey, 
  FaBold, FaItalic, FaUnderline, FaListUl, FaListOl,
  FaHeading, FaCode, FaLink, FaImage, FaQuoteLeft,
  FaEye, FaEyeSlash, FaExpand, FaCompress, FaBug,
  FaStickyNote, FaInfoCircle, FaExclamationTriangle,
  FaDatabase, FaServer, FaNetworkWired, FaList,
  FaCheckCircle, FaExclamationCircle
} from 'react-icons/fa';
import api from '../services/api';
import { useCrypto } from '../contexts/CryptoContext';
import { useAuth } from '../contexts/AuthContext';
import { encryptNote, decryptNote } from '../utils/cryptoUtils';
import axios from 'axios';

const NoteEditor = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isNewNote = id === 'new';

  const { state } = useLocation(); 

  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [tags, setTags] = useState('');
  const [loading, setLoading] = useState(!isNewNote);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);
  const [versions, setVersions] = useState([]);
  const [isAutoResizing, setIsAutoResizing] = useState(true);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [showPreview, setShowPreview] = useState(false);
  const [debugInfo, setDebugInfo] = useState(null);
  const [apiResponse, setApiResponse] = useState(null);
  const [apiError, setApiError] = useState(null);
  const [backendStatus, setBackendStatus] = useState('unknown');

  const textareaRef = useRef(null);
  const editorRef = useRef(null);
  const { masterKey, encrypt, decrypt } = useCrypto();
  const { user, isAuthenticated, logout, loading: authLoading } = useAuth();

  useEffect(() => {
  if (isNewNote && state) {
    console.log('üîç NoteEditor: Received prefilled data from Dashboard:', state);
    if (state.prefillTitle) {
      setTitle(state.prefillTitle);
    }
    if (state.prefillTags) {
      setTags(state.prefillTags.join(', '));
    }
    if (state.shouldEncrypt && !masterKey) {
      // Prompt for encryption password if needed
      setShowPasswordPrompt(true);
    }
  }
}, [isNewNote, state]);

  // Debug: Log authentication state
  useEffect(() => {
    console.log('üîç NoteEditor: Component mounted');
    console.log('üîç NoteEditor: isAuthenticated:', isAuthenticated);
    console.log('üîç NoteEditor: User:', user);
    console.log('üîç NoteEditor: User ID:', user?._id);
    console.log('üîç NoteEditor: Token in localStorage:', localStorage.getItem('token') ? 'Present' : 'Missing');
    console.log('üîç NoteEditor: Auth loading:', authLoading);
    console.log('üîç NoteEditor: Note ID from URL:', id);
    console.log('üîç NoteEditor: Is new note?', isNewNote);

    // Check axios headers
    console.log('üîç NoteEditor: Axios headers:', axios.defaults.headers.common);

    // Update debug info
    updateDebugInfo();

    // Check backend status
    checkBackendStatus();
  }, [isAuthenticated, user, authLoading, id, isNewNote]);

  useEffect(() => {
    // Debug: Show current state
    console.log('=== NOTE EDITOR AUTH CHECK ===');
    console.log('authLoading:', authLoading);
    console.log('isAuthenticated:', isAuthenticated);
    console.log('isNewNote:', isNewNote);
    console.log('Note ID:', id);

    // Wait for auth to finish loading
    if (authLoading) {
      console.log('üîç NoteEditor: Auth still loading, waiting...');
      return;
    }

    // Check authentication
    if (!isAuthenticated) {
      console.log('üîç NoteEditor: User not authenticated, redirecting to login');
      setError('Please log in to create or edit notes');
      navigate('/login');
      return;
    }

    console.log('üîç NoteEditor: User authenticated, proceeding...');
    console.log('üîç NoteEditor: User details:', {
      id: user?._id,
      email: user?.email
    });

    if (!isNewNote) {
      fetchNote();
    } else {
      setLoading(false);
    }
  }, [id, isAuthenticated, navigate, authLoading, isNewNote, user]);

  // Auto-resize textarea based on content
  const resizeTextarea = () => {
    if (textareaRef.current && isAutoResizing) {
      textareaRef.current.style.height = 'auto';
      const newHeight = Math.max(400, textareaRef.current.scrollHeight);
      textareaRef.current.style.height = `${newHeight}px`;
    }
  };

  useEffect(() => {
    resizeTextarea();
  }, [content]);

  // Check backend status
  const checkBackendStatus = async () => {
    try {
      // Try to ping the backend
      const response = await axios.get('http://localhost:5000/', {
        timeout: 2000
      });
      setBackendStatus('connected');
      console.log('‚úÖ Backend is connected');
    } catch (error) {
      setBackendStatus('disconnected');
      console.warn('‚ö†Ô∏è Backend connection check failed:', error.message);
    }
  };

  // Debug function to check note ID
  const debugNoteId = () => {
    const info = {
      idFromParams: id,
      type: typeof id,
      length: id?.length,
      isNewNote: id === 'new',
      fullURL: window.location.href,
      pathname: window.location.pathname,
      isValidObjectId: id && id !== 'new' ? /^[0-9a-fA-F]{24}$/.test(id) : false
    };

    console.log('üîç Debug Note ID:', info);

    alert(`Note ID Debug:\n\n` +
          `ID: "${id}"\n` +
          `Type: ${typeof id}\n` +
          `Length: ${id?.length}\n` +
          `Is new note? ${id === 'new' ? '‚úÖ Yes' : '‚ùå No'}\n` +
          `Valid ObjectId? ${info.isValidObjectId ? '‚úÖ Yes' : '‚ùå No'}\n` +
          `Full URL: ${window.location.href}\n` +
          `Pathname: ${window.location.pathname}`);
  };

  // Test API call directly (bypassing the api service)
  const testDirectApiCall = async () => {
    try {
      setApiError(null);
      setApiResponse(null);

      const token = localStorage.getItem('token');
      console.log('üîç Direct API Test:');
      console.log('Token:', token ? `${token.substring(0, 20)}...` : 'Missing');
      console.log('Note ID:', id);
      console.log('User ID:', user?._id);

      if (!token) {
        throw new Error('No token found in localStorage');
      }

      // Better validation for note ID
      if (!id || id === 'new' || id === 'undefined' || id === 'null') {
        throw new Error(`Cannot test API: This is a new note (ID: "${id}"). Create a note first, then edit it.`);
      }

      // Check if ID looks like a MongoDB ObjectId (24 hex characters)
      const isValidObjectId = /^[0-9a-fA-F]{24}$/.test(id);
      if (!isValidObjectId) {
        throw new Error(`Invalid note ID format: "${id}". Expected 24-character hex string. Visit /notes to see your notes.`);
      }

      console.log('üîç Making direct API call to:', `http://localhost:5000/api/notes/${id}`);

      const response = await axios.get(`http://localhost:5000/api/notes/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        timeout: 5000
      });

      console.log('‚úÖ Direct API Success:', response.data);
      setApiResponse(response.data);

      alert(`‚úÖ Direct API call successful!\n\nTitle: ${response.data.title}\nID: ${response.data._id}\nOwner: ${response.data.ownerId}`);

      return response.data;
    } catch (error) {
      const errorDetails = {
        status: error.response?.status,
        statusText: error.response?.statusText,
        data: error.response?.data,
        message: error.message,
        code: error.code
      };

      console.error('‚ùå Direct API Error:', errorDetails);
      setApiError(errorDetails);

      let errorMessage = `Direct API test failed:\n\n`;
      errorMessage += `Status: ${errorDetails.status || 'No response'}\n`;
      errorMessage += `Message: ${errorDetails.message}\n`;

      if (errorDetails.data) {
        errorMessage += `Error: ${JSON.stringify(errorDetails.data, null, 2)}\n`;
      }

      if (errorDetails.status === 404) {
        errorMessage += `\nüí° Tip: This note doesn't exist or you don't have access. Visit /notes to see your notes.`;
      } else if (errorDetails.code === 'ECONNREFUSED') {
        errorMessage += `\nüí° Tip: Backend server is not running on port 5000. Start it with: cd backend && npm start`;
      }

      alert(errorMessage);

      return null;
    }
  };

  // Test backend connectivity
  const testBackendHealth = async () => {
    try {
      console.log('üîç Testing backend health...');

      // Try multiple endpoints to see what works
      const endpoints = [
        { url: 'http://localhost:5000/', name: 'Root' },
        { url: 'http://localhost:5000/api/auth/me', name: 'Auth Check' },
        { url: 'http://localhost:5000/api/notes', name: 'Notes List' }
      ];

      let success = false;
      let errorMessage = '';
      let workingEndpoint = null;

      for (const endpoint of endpoints) {
        try {
          console.log(`Trying ${endpoint.name} (${endpoint.url})...`);
          const response = await axios.get(endpoint.url, {
            timeout: 2000,
            headers: endpoint.url.includes('/auth/me') && localStorage.getItem('token') ? {
              'Authorization': `Bearer ${localStorage.getItem('token')}`
            } : {}
          });
          console.log(`‚úÖ ${endpoint.name} responded:`, response.status);
          success = true;
          workingEndpoint = endpoint;
          break;
        } catch (err) {
          errorMessage = err.message;
          console.log(`‚ùå ${endpoint.name} failed:`, err.message);

          // If it's a 401, backend is running but token is invalid
          if (err.response?.status === 401) {
            success = true;
            workingEndpoint = endpoint;
            errorMessage = 'Backend is running but token is invalid/expired';
            break;
          }
        }
      }

      if (success) {
        setBackendStatus('connected');
        if (workingEndpoint) {
          alert(`‚úÖ Backend is running!\n\nEndpoint: ${workingEndpoint.name}\nURL: ${workingEndpoint.url}\n\nStatus: Connected`);
        }
      } else {
        setBackendStatus('disconnected');
        throw new Error(`All endpoints failed. Last error: ${errorMessage}`);
      }
    } catch (error) {
      console.error('‚ùå Backend health check failed:', error.message);
      setBackendStatus('disconnected');
      alert(`‚ùå Cannot connect to backend:\n\n${error.message}\n\nMake sure:\n1. Backend server is running on port 5000\n2. Run: cd backend && npm start\n3. Check if port 5000 is available`);
    }
  };

  // List all notes to see what's available
  const listAllNotes = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('‚ùå No token found. Please log in first.');
        return;
      }

      console.log('üîç Listing all notes...');
      const response = await axios.get('http://localhost:5000/api/notes', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        timeout: 5000
      });

      console.log('‚úÖ Notes list:', response.data);

      if (response.data.length === 0) {
        alert('üìù No notes found. Create a new note first!');
        return;
      }

      let message = `üìö Found ${response.data.length} notes:\n\n`;
      response.data.forEach((note, index) => {
        message += `${index + 1}. ${note.title || 'Untitled'} \n`;
        message += `   ID: ${note._id}\n`;
        message += `   Created: ${new Date(note.createdAt).toLocaleDateString()}\n`;
        message += `   Tags: ${note.tags?.join(', ') || 'None'}\n\n`;
      });

      message += `\nüí° Tip: Copy a note ID and visit /note/[id] to edit it.`;

      alert(message);
    } catch (error) {
      console.error('‚ùå Failed to list notes:', error);
      let errorMsg = `Failed to list notes:\n\n${error.message}`;
      if (error.response?.status === 401) {
        errorMsg += '\n\nYour session may have expired. Please log in again.';
      } else if (error.code === 'ECONNREFUSED') {
        errorMsg += '\n\nBackend server is not running. Start it with: cd backend && npm start';
      }
      alert(errorMsg);
    }
  };

  const fetchNote = async () => {
    try {
      // If it's a new note, don't try to fetch
      if (isNewNote) {
        setLoading(false);
        return;
      }

      setLoading(true);
      setError('');
      setApiError(null);
      setApiResponse(null);

      console.log('üîç NoteEditor: Fetching note with ID:', id);
      console.log('üîç NoteEditor: User ID:', user?._id);

      // Check if ID is literally "undefined" string
      if (id === "undefined") {
        const errorMsg = 'Invalid note URL. The note ID is missing. Go to /notes to see your notes or /note/new to create one.';
        console.error('üîç NoteEditor:', errorMsg);
        setError(errorMsg);
        setLoading(false);
        return;
      }

      // Validate ID format
      if (!id || !/^[0-9a-fA-F]{24}$/.test(id)) {
        const errorMsg = `Invalid note ID format: "${id}". Expected 24-character hex string. Visit /notes to see your notes.`;
        console.error('üîç NoteEditor:', errorMsg);
        setError(errorMsg);
        throw new Error(errorMsg);
      }

      console.log('üîç NoteEditor: Using api.getNote() service...');

      const response = await api.getNote(id);
      console.log('üîç NoteEditor: Note fetched successfully:', response.data);

      const note = response.data;
      setApiResponse(note);

      // Check if note is actually returned
      if (!note) {
        const errorMsg = 'Note not found in response (null/undefined)';
        console.error('üîç NoteEditor:', errorMsg);
        setError(errorMsg);
        throw new Error(errorMsg);
      }

      console.log('üîç NoteEditor: Note details:', {
        id: note._id,
        title: note.title,
        ownerId: note.ownerId,
        hasContent: !!note.content,
        isEncrypted: note.isEncrypted,
        tagsCount: note.tags?.length || 0
      });

      setTitle(note.title || '');
      setTags(note.tags?.join(', ') || '');

      // Decrypt content if we have the master key
      if (masterKey && note.isEncrypted && note.encryptionMetadata) {
        try {
          console.log('üîç NoteEditor: Attempting to decrypt note content');
          const decryptedContent = decryptNote(
            note.content,
            masterKey,
            note.encryptionMetadata.iv
          );
          setContent(decryptedContent);
          console.log('üîç NoteEditor: Note decrypted successfully');
        } catch (decryptError) {
          console.error('üîç NoteEditor: Decryption failed:', decryptError);
          setError('Failed to decrypt note. Check your master password.');
          setContent('[Encrypted - Enter master password to view]');
        }
      } else {
        setContent(note.content || '');
      }
    } catch (error) {
      console.error('üîç NoteEditor: Failed to fetch note:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
        statusText: error.response?.statusText,
        headers: error.response?.headers,
        config: {
          url: error.config?.url,
          method: error.config?.method,
          headers: error.config?.headers
        }
      });

      setApiError({
        status: error.response?.status,
        statusText: error.response?.statusText,
        data: error.response?.data,
        message: error.message
      });

      if (error.response?.status === 401) {
        setError('Session expired. Please log in again.');
        logout();
        navigate('/login');
      } else if (error.response?.status === 404) {
        setError(`Note not found (ID: ${id}). It may have been deleted or you don't have access. Visit /notes to see your notes.`);
      } else if (error.response?.status === 500) {
        setError('Server error (500). Please check backend logs and make sure MongoDB is running.');
      } else if (error.code === 'ECONNREFUSED') {
        setError('Cannot connect to backend server. Make sure it\'s running on http://localhost:5000');
      } else if (error.message?.includes('Network Error')) {
        setError('Network error. Check your internet connection and backend server.');
      } else if (error.message?.includes('Invalid note ID format')) {
        setError(error.message);
      } else {
        setError(`Failed to load note: ${error.message || 'Unknown error'}`);
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchVersions = async () => {
    try {
      const response = await api.getNoteVersions(id);
      setVersions(response.data.versions);
      setShowHistory(true);
    } catch (error) {
      console.error('Failed to fetch versions:', error);
    }
  };

  const updateDebugInfo = async () => {
    const token = localStorage.getItem('token');
    const info = {
      timestamp: new Date().toISOString(),
      authLoading,
      isAuthenticated,
      user: user ? { 
        email: user.email, 
        id: user._id || user.id,
        hasId: !!(user._id || user.id)
      } : null,
      hasToken: !!token,
      tokenLength: token?.length || 0,
      tokenPreview: token ? `${token.substring(0, 10)}...${token.substring(token.length - 10)}` : null,
      masterKey: !!masterKey,
      noteId: id,
      isNewNote,
      isValidObjectId: id && id !== 'new' ? /^[0-9a-fA-F]{24}$/.test(id) : false,
      backendStatus,
      apiResponse: apiResponse ? {
        hasData: true,
        id: apiResponse._id,
        title: apiResponse.title,
        ownerId: apiResponse.ownerId
      } : null,
      apiError: apiError ? {
        status: apiError.status,
        message: apiError.message
      } : null
    };

    setDebugInfo(info);
    console.log('üîç Debug Info Updated:', info);
  };

  const debugAuth = async () => {
    console.log('=== DEBUG AUTHENTICATION ===');
    console.log('1. LocalStorage token:', localStorage.getItem('token'));
    console.log('2. isAuthenticated:', isAuthenticated);
    console.log('3. User:', user);
    console.log('4. User ID:', user?._id);
    console.log('5. Axios headers:', axios.defaults.headers.common);
    console.log('6. Auth loading:', authLoading);

    const token = localStorage.getItem('token');
    if (!token) {
      console.log('ERROR: No token in localStorage');
      alert('‚ùå No token found. Please log in again.');
      return;
    }

    try {
      console.log('Testing token with backend...');
      const response = await axios.get('http://localhost:5000/api/auth/me', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      console.log('‚úÖ Token is valid:', response.data);
      alert('‚úÖ Token is valid!\n\nUser: ' + response.data.user.email + '\nID: ' + response.data.user._id);
    } catch (error) {
      console.error('‚ùå Token test failed:', {
        status: error.response?.status,
        message: error.message,
        data: error.response?.data
      });
      alert('‚ùå Token validation failed: ' + (error.response?.data?.error || error.message));
    }
  };

  const handleSave = async () => {
    console.log('üîç NoteEditor: Save button clicked');

    if (!title.trim()) {
      setError('Title is required');
      return;
    }

    // Check if we have a token
    const token = localStorage.getItem('token');
    if (!token) {
      console.log('üîç NoteEditor: No token found, redirecting to login');
      setError('Please log in to save notes');
      navigate('/login');
      return;
    }

    setSaving(true);
    setError('');

    try {
      const noteData = {
        title: title.trim(),
        tags: tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        isEncrypted: !!masterKey
      };

      // Encrypt content if we have master key
      if (masterKey) {
        try {
          const encrypted = encryptNote(content, masterKey);
          noteData.content = encrypted.ciphertext;
          noteData.encryptionMetadata = {
            algorithm: 'AES-CBC',
            iv: encrypted.iv,
            salt: encrypted.salt
          };
        } catch (encryptError) {
          console.error('üîç NoteEditor: Encryption error:', encryptError);
          setError('Failed to encrypt note. Using plaintext.');
          noteData.content = content;
          noteData.isEncrypted = false;
        }
      } else {
        noteData.content = content;
        noteData.isEncrypted = false;
      }

      console.log('üîç NoteEditor: Sending note data:', {
        title: noteData.title,
        hasContent: !!noteData.content,
        isEncrypted: noteData.isEncrypted,
        tagsCount: noteData.tags?.length || 0
      });

      // Use the api service which should have headers set
      let response;
      if (isNewNote) {
        response = await api.createNote(noteData);
      } else {
        response = await api.updateNote(id, noteData);
      }

      console.log('üîç NoteEditor: Save successful:', response.data);
      alert('‚úÖ Note saved successfully!');

      if (isNewNote) {
        navigate(`/note/${response.data.id}`);
      }

    } catch (error) {
      console.error('üîç NoteEditor: Save error:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });

      if (error.response?.status === 401) {
        setError('Session expired. Please log in again.');
        localStorage.removeItem('token');
        window.location.href = '/login';
      } else if (error.code === 'ECONNREFUSED') {
        setError('Cannot connect to backend server. Make sure it\'s running on http://localhost:5000');
      } else {
        setError(error.response?.data?.error || 'Failed to save note');
      }
    } finally {
      setSaving(false);
    }
  };

  const handleRestoreVersion = async (version) => {
